<?phpif (!defined('BASEPATH'))	exit('No direct script access allowed');class Adminlayout {	private $_template_f;	private $CI,$userinfo,$yoursite, $site_selected, $language, $lang_selected;	public function __construct() {		$this->CI = & get_instance();		$this->_template_f = $this->CI->config->item('template_f');		$this->CI->load->library('Layout');		$this->CI->load->model('admin/Model_lang');		$this->CI->layout->css('publics/admin/bootstrap/css/bootstrap.min.css');		$this->CI->layout->css('https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css');		$this->CI->layout->css('http://code.ionicframework.com/ionicons/2.0.0/css/ionicons.min.css');		$this->CI->layout->css('publics/admin/dist/css/AdminLTE.min.css');		$this->CI->layout->css('publics/admin/dist/css/style.css');		$this->CI->layout->css('publics/admin/plugins/datatables/dataTables.bootstrap.css');		$this->CI->layout->css('publics/admin/dist/css/skins/_all-skins.min.css');		$this->CI->layout->css('publics/admin/plugins/iCheck/flat/blue.css');		$this->CI->layout->css('publics/admin/plugins/morris/morris.css');		$this->CI->layout->css('publics/admin/plugins/jvectormap/jquery-jvectormap-1.2.2.css');		$this->CI->layout->css('publics/admin/plugins/datepicker/datepicker3.css');		$this->CI->layout->css('publics/admin/plugins/daterangepicker/daterangepicker-bs3.css');		$this->CI->layout->css('publics/admin/plugins/bootstrap-wysihtml5/bootstrap3-wysihtml5.min.css');				$this->CI->layout->js('publics/admin/plugins/jQuery/jQuery-2.1.3.min.js');		$this->CI->layout->js('publics/admin/bootstrap/js/bootstrap.min.js');		$this->CI->layout->js('publics/admin/plugins/datepicker/bootstrap-datepicker.js');	    $this->CI->layout->js('publics/admin/plugins/datatables/jquery.dataTables.js');	    $this->CI->layout->js('publics/admin/plugins/datatables/dataTables.bootstrap.js');	    // $this->CI->layout->js('publics/admin/plugins/slimScroll/jquery.slimscroll.min.js');		$this->CI->layout->js('publics/admin/plugins/fastclick/fastclick.min.js');		// $this->CI->layout->js('publics/admin/dist/js/app.js');		$this->CI->layout->js('publics/admin/dist/js/app.min.js');	}	public function loadTop($viewFile = 'backend/admin/top_view', $data = array()){		$authentication = $this->CI->session->userdata('authentication');		if(isset($authentication) && !empty($authentication))		{			$guser = json_decode($authentication,TRUE);			$userID = $guser['id'];			$this->userinfo = $this->CI->permit_model->getINFO($userID);			$this->CI->session->set_userdata('userinfo',$this->userinfo);		}		$data['showuserinfo'] = $this->userinfo;		$this->lang_selected = $this->CI->session->userdata('lang_select');		$data['lang_selected']=$this->lang_selected;		$this->language = $this->CI->Model_lang->getAllLang();		$data['language'] = $this->language;				$this->CI->load->model('admin/comment_model');		$data['total_comment_pending'] = $this->CI->comment_model->getcount(array('site_id'=>$this->site_selected,'status'=>0));		return $this->CI->load->view($this->_template_f . $viewFile, $data, true);	}	function loadMenu($current = 'treeview', $viewFile = 'backend/admin/menu_view',  $data = array()){		$menu =  $this->CI->load->view($this->_template_f . $viewFile, $data, true);		$menu = str_replace('class_'.$current, 'active', $menu);		return $menu;	}	function loadFooter($viewFile = 'backend/admin/footer_view', $data = array()){		return $this->CI->load->view($this->_template_f . $viewFile, $data, true);	}}