INSERT INTO `utt_post` (`id`, `title`, `image`, `description`, `detail`, `site_id`, `lang`, `status`, `user_id`, `post_type`, `cate_id`, `time_create`, `time_update`, `sort`, `is_top`) VALUES
(16, 'Hội thảo tư vấn giới thiệu việc làm và phỏng vấn tuyển dụng của doanh nghiệp Nhật Bản', 'truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-031.jpg', 'Sáng 19 tháng 8 năm 2015, Trường Đại học Công nghệ GTVT đã phối hợp với các Doanh nghiệp Nhật Bản tổ chức Hội thảo tư vấn giới thiệu việc làm và phỏng vấn tuyển dụng, nhằm cung cấp cho sinh viên những thông tin cần thiết về vấn đề tuyển dụng làm việc tại Nhật Bản.', '<p>S&aacute;ng 19 th&aacute;ng 8 năm 2015, T<a href="http://utt.edu.vn/">rường Đại học C&ocirc;ng nghệ GTVT</a>&nbsp;đ&atilde; phối hợp với c&aacute;c Doanh nghiệp Nhật Bản tổ chức Hội thảo tư vấn giới thiệu việc l&agrave;m v&agrave; phỏng vấn tuyển dụng, nhằm cung cấp cho sinh vi&ecirc;n những th&ocirc;ng tin cần thiết về vấn đề tuyển dụng l&agrave;m việc tại Nhật Bản.</p>\r\n\r\n<p>Tham dự Hội thảo có đại di&ecirc;̣n Vụ TCCB, Vụ HTQT- B&ocirc;̣ GTVT; về ph&iacute;a c&aacute;c Doanh nghiệp Nhật Bản c&oacute; Ng&agrave;i&nbsp;Go Oyaymada - Tổng Gi&aacute;m đốc C&ocirc;ng ty TNHH S.S.Sangyo v&agrave; Ng&agrave;i&nbsp;Fumikata Matsubara - T&ocirc;̉ng giám đ&ocirc;́c C&ocirc;ng ty TNHH Matsubara; về ph&iacute;a Trường Đại học C&ocirc;ng nghệ GTVT c&oacute; PGS.TS. Đ&agrave;o Văn Đ&ocirc;ng - Hiệu trưởng Nh&agrave; trường, TS. Vũ Ngọc Khi&ecirc;m - Ph&oacute; Hiệu trưởng Nh&agrave; trường, l&atilde;nh đạo c&aacute;c đơn vị chức năng trong Trường v&agrave;&nbsp;hơn 200 sinh vi&ecirc;n đang theo học tại Khoa Cơ khí, Khoa C&ocirc;ng ngh&ecirc;̣ th&ocirc;ng tin quan t&acirc;m v&agrave; tham gia hội thảo.</p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-03.jpg" style="height:500px; width:750px" /><br />\r\n<em>Ng&agrave;i&nbsp;Go Oyamada - Tổng Gi&aacute;m đốc C&ocirc;ng ty TNHH S.S.Sangyo ph&aacute;t biểu tại hội thảo</em></p>\r\n\r\n<p>C&ocirc;ng ty TNHH S.S.Sangyo được th&agrave;nh lập từ năm 1991 tại tỉnh Aichi, Nhật Bản. Đ&acirc;y l&agrave; doanh nghiệp kinh doanh nh&acirc;n lực đ&atilde; được đ&agrave;o tạo, với triết l&yacute; kinh doanh l&agrave; &quot;V&igrave; sự hạnh ph&uacute;c về tinh thần v&agrave; thể chất của nh&acirc;n vi&ecirc;n. C&ocirc;́ng hiến cho x&atilde; hội th&ocirc;ng qua hoạt động doanh nghiệp&quot;. C&ocirc;ng ty TNHH Matsubara nằm tại tỉnh Gifu, Nhật Bản, lĩnh vực hoạt động chủ yếu của c&ocirc;ng ty l&agrave; sản xuất c&aacute;c sản phẩm linh kiện thuộc nhiều ng&agrave;nh c&ocirc;ng nghệ kh&aacute;c nhau, trong đ&oacute; xe hơi chiếm khoảng 70%. Đặc biệt tại tỉnh Gifu c&oacute; m&ocirc;i trường y&ecirc;n tĩnh, được bao bọc bởi thi&ecirc;n nhi&ecirc;n, gi&aacute; cả rẻ v&agrave; ổn định hơn so với c&aacute;c th&agrave;nh phố lớn như Tokyo...rất thuận lợi cho c&aacute;c lao động l&agrave;m việc tại đ&acirc;y.</p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-02.jpg" style="height:500px; width:750px" /><br />\r\n<em>PGS.TS. Đ&agrave;o Văn Đ&ocirc;ng - Hiệu trưởng Nh&agrave; trường ph&aacute;t biểu tại hội thảo</em></p>\r\n\r\n<p>Ph&aacute;t biểu khai mạc Hội thảo, PGS.TS. Đ&agrave;o Văn Đ&ocirc;ng đ&aacute;nh gi&aacute; cao mối quan hệ giữa Nh&agrave; trường với c&aacute;c Doanh nghiệp, đặc biệt l&agrave; c&aacute;c Doanh nghiệp tới từ Nhật Bản, một trong những cường quốc về kinh tế tr&ecirc;n thế giới. Doanh nghiệp l&agrave; nơi sử dụng c&aacute;c bạn sinh vi&ecirc;n đ&atilde; được đ&agrave;o tạo từ Nh&agrave; trường, nơi để sinh vi&ecirc;n &aacute;p dụng c&aacute;c kiến thức đ&atilde; được học tập tr&ecirc;n giảng đường đại học v&agrave;o c&ocirc;ng việc cụ thể tại doanh nghiệp.&nbsp;Hội thảo lần n&agrave;y l&agrave; kết quả của <a href="http://utt.edu.vn/home/hop-tac/hop-tac-quoc-te/1420-doan-cong-tac-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-nhat-ban">chuyến c&ocirc;ng t&aacute;c tại Nhật Bản</a>&nbsp;của Đo&agrave;n đại biểu Nh&agrave; trường trong th&aacute;ng 6 vừa qua, với mong muốn cho c&aacute;c em sinh vi&ecirc;n t&igrave;m hiểu th&ocirc;ng tin về c&aacute;c doanh nghiệp, c&oacute; cơ hội tiếp x&uacute;c v&agrave; được trực tiếp phỏng vấn tuyển dụng sang l&agrave;m việc tại đất nước Nhật Bản.&nbsp;Thầy Hiệu trưởng cũng nhắn nhủ tới c&aacute;c&nbsp;bạn sinh vi&ecirc;n ngo&agrave;i việc học tập tốt kiến thức chuy&ecirc;n m&ocirc;n, c&aacute;c em cũng cần phải r&egrave;n luyện th&ecirc;m ngoại ngữ, c&aacute;c kỹ năng mềm v&agrave; đặc biệt hơn l&agrave; tu dưỡng đạo đức, n&acirc;ng cao &yacute; thức kỷ luật để c&oacute; thể l&agrave;m việc trong m&ocirc;i trường c&ocirc;ng nghiệp kh&ocirc;ng chỉ ở c&aacute;c doanh nghiệp trong nước m&agrave; c&ograve;n ở c&aacute;c doanh nghiệp nước ngo&agrave;i.</p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-04.jpg" style="height:500px; width:750px" /><br />\r\n<em>C&aacute;c bạn sinh vi&ecirc;n tham gia đặt c&acirc;u hỏi cho c&aacute;c Doanh nghiệp Nhật Bản tại&nbsp;hội thảo</em></p>\r\n\r\n<p>Tại buổi Hội thảo, hai&nbsp;c&ocirc;ng ty đ&atilde; giới thiệu tới c&aacute;c bạn sinh vi&ecirc;n về đất nước v&agrave; con người Nhật Bản, m&ocirc;i trường l&agrave;m việc tại c&aacute;c nh&agrave; m&aacute;y cũng như&nbsp;t&aacute;c phong l&agrave;m việc của con người tại Nhật Bản. C&aacute;c bạn sinh vi&ecirc;n rất hứng th&uacute; với b&agrave;i thuyết tr&igrave;nh v&agrave; đặc biệt ấn tượng với t&aacute;c phong l&agrave;m việc của con người Nhật Bản với ti&ecirc;u ch&iacute; chất lượng, ch&iacute;nh x&aacute;c, đ&uacute;ng kế hoạch lu&ocirc;n được ưu ti&ecirc;n h&agrave;ng đầu.</p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-05.jpg" style="height:500px; width:750px" /><br />\r\n<em>Ng&agrave;i&nbsp;Go Oyamada, Tổng Gi&aacute;m đốc - C&ocirc;ng ty TNHH S.S.Sangyo phỏng vấn c&aacute;c bạn sinh vi&ecirc;n</em></p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-06.jpg" style="height:500px; width:750px" /><br />\r\n<em>Ng&agrave;i&nbsp;Fumitaka Matsubara - Tổng Gi&aacute;m đốc C&ocirc;ng ty TNHH Matsubara phỏng vấn c&aacute;c bạn sinh vi&ecirc;n</em></p>\r\n\r\n<p>Sau buổi hội thảo, đại diện hai c&ocirc;ng ty TNHH S.S.Sangyo v&agrave; TNHH Matsubara đ&atilde; c&oacute; buổi phỏng vấn trực tiếp c&aacute;c bạn sinh vi&ecirc;n quan t&acirc;m v&agrave; c&oacute; nhu cầu t&igrave;m kiếm cơ hội l&agrave;m việc tại Nhật Bản. Buổi phỏng vấn được đ&aacute;nh gi&aacute; l&agrave; rất th&agrave;nh c&ocirc;ng, c&aacute;c bạn sinh vi&ecirc;n để lại ấn tượng s&acirc;u sắc với hai vị Tổng Gi&aacute;m đốc của hai c&ocirc;ng ty. Với sự tự tin, năng lực bản th&acirc;n cũng như c&aacute;c kiến thức được học tr&ecirc;n giảng đường <strong>Đại học C&ocirc;ng nghệ GTVT</strong>,&nbsp;hi vọng c&aacute;c bạn sẽ&nbsp;c&oacute; cơ hội được l&agrave;m việc tại Nhật Bản trong tương lai kh&ocirc;ng xa.</p>\r\n\r\n<p><img alt="Hội thảo tư vấn giới thiệu việc làm với doanh nghiệp Nhật Bản" src="http://utt.edu.vn/home/images/stories/truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-07.jpg" style="height:500px; width:750px" /></p>', 1, 'vn', 1, 8, 'news', 1, 1441076890, NULL, NULL, 1),
(17, 'Sinh viên UTT đạt Chứng chỉ kỹ năng nghề thế giới xuất sắc tại kỳ thi Tay nghề thế giới lần thứ 43', 'tay-nghe-the-gioi-nguyen-duc-thanh-2015-05.jpg', 'Xin chúc mừng sinh viên Nguyễn Đức Thành, lớp 62CCOT04, Ngành Công nghệ kỹ thuật ô tô, Trường Đại học Công nghệ GTVT đạt Chứng chỉ Kỹ năng nghề thế giới xuất sắc môn Thiết kế kỹ thuật cơ khí- CAD tại Kỳ thi Tay nghề thế giới lần thứ 43 năm 2015 tổ chức tại Sao Paulo, Brazil.', '<p>Xin ch&uacute;c mừng sinh vi&ecirc;n Nguyễn Đức Th&agrave;nh, lớp 62CCOT04, <a href="http://utt.edu.vn/home/tuyen-sinh/tuyen-sinh-2015/1444-chi-tieu-tuyen-sinh-cac-nganh-nam-2015"><strong>Ng&agrave;nh C&ocirc;ng nghệ kỹ thuật &ocirc; t&ocirc;</strong></a>, Trường Đại học C&ocirc;ng nghệ GTVT đạt Chứng chỉ Kỹ năng nghề thế giới xuất sắc m&ocirc;n Thiết kế kỹ thuật cơ kh&iacute;- CAD tại&nbsp;<a href="http://utt.edu.vn/home/tin-tuc/tin-tuc-nha-truong/1485-sinh-vien-utt-dat-giai-cao-tai-ky-thi-tay-nghe-the-gioi-lan-thu-43">Kỳ thi Tay nghề thế giới</a> lần thứ 43 năm 2015 tổ chức tại Sao Paulo, Brazil.</p>\r\n\r\n<p><img alt="" src="http://utt.edu.vn/home/images/stories/tay-nghe-the-gioi-nguyen-duc-thanh-2015-03.jpg" style="display:block; height:525px; margin-left:auto; margin-right:auto; width:700px" /></p>\r\n\r\n<p>Cuộc thi kết th&uacute;c sau 4 ng&agrave;y tranh t&agrave;i với gần 1200 đối thủ từ 60 Quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ. Đo&agrave;n Việt Nam gồm 14 th&iacute; sinh, thi 13 nghề v&agrave; lần đầu ti&ecirc;n đạt Huy chương đồng m&ocirc;n Giải ph&aacute;p phần mềm c&ocirc;ng nghệ th&ocirc;ng tin sau 4 lần tham dự.</p>\r\n\r\n<p><img alt="" src="http://utt.edu.vn/home/images/stories/tay-nghe-the-gioi-nguyen-duc-thanh-2015-04.jpg" style="display:block; height:525px; margin-left:auto; margin-right:auto; width:700px" /></p>\r\n\r\n<p>Sinh vi&ecirc;n Nguyễn Đức Th&agrave;nh đang thực hiện b&agrave;i thi của m&igrave;nh.</p>\r\n\r\n<p><img alt="" src="http://utt.edu.vn/home/images/stories/tay-nghe-the-gioi-nguyen-duc-thanh-2015-05.jpg" style="display:block; height:525px; margin-left:auto; margin-right:auto; width:700px" /></p>\r\n\r\n<p>Thành là thí sinh Vi&ecirc;̣t Nam đ&acirc;̀u ti&ecirc;n đạt Chứng chỉ Kỹ năng nghề thế giới xuất sắc m&ocirc;n Thiết kế kỹ thuật cơ kh&iacute;- CAD</p>\r\n\r\n<p>&nbsp;<img alt="Đoàn công tác nghề Thiết kế kỹ thuật cơ khí - CAD trường Đại học Công nghệ GTVT làm việc tại Hàn Quốc" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-02.jpg" style="height:499px; width:750px" /></p>\r\n\r\n<p>Trước khi đến với cuộc thi thế giới, Th&agrave;nh tham gia cuộc thi Tay nghề Quốc gia v&agrave; đ&atilde; nhận được học bổng 80.000 USD do SamSung t&agrave;i trợ 1 năm luyện t&acirc;̣p tại Trung t&acirc;m huấn luyện kỹ năng Samsung (Samsung Skills Training Center) &ndash; Samsung Digital City - Th&agrave;nh phố Suwoul &ndash; H&agrave;n Quốc.</p>\r\n\r\n<p><img alt="doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-01.jpg" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-01.jpg" style="height:499px; width:750px" /></p>', 1, 'vn', 1, 8, 'news', 1, 1441076978, NULL, NULL, 1),
(18, 'Thư chúc mừng cán bộ, công chức, viên chức và người lao động ngành GTVT', '35624f0422ac91dae9a19405f96ff986_w250_h170_cp1.jpg', 'Thư chúc mừng cán bộ, công chức, viên chức và người lao động ngành Giao thông vận tải của Thủ tướng Chính phủ nhân dịp kỷ niệm 70 năm Ngày truyền thống ngành GTVT:', '<p>Thư ch&uacute;c mừng c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức v&agrave; người lao động ng&agrave;nh Giao th&ocirc;ng vận tải của Thủ tướng Ch&iacute;nh phủ nh&acirc;n dịp kỷ niệm 70 năm Ng&agrave;y truyền thống ng&agrave;nh GTVT:</p>\\r\\n\\r\\n<p><img alt=\\"Thư chúc mừng cán bộ, công chức, viên chức và người lao động ngành GTVT\\" src=\\"http://utt.edu.vn/home/images/stories/thu-chuc-mung-nganh-gtvt-cua-thu-tuong-chinh-phu.jpg\\" /></p>', 1, 'vn', 2, 8, 'news', 5, 1441077028, 1441415638, NULL, 1),
(20, 'Đoàn công tác nghề Thiết kế kỹ thuật cơ khí - CAD trường Đại học Công nghệ GTVT làm việc tại Hàn Quốc', 'doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-03.jpg', 'Kỳ thi tay nghề thế giới lần thứ 43 sẽ được tổ chức tại Sao Paulo, Brazil từ ngày 5 đến ngày 17/8/2015 ở 50 nghề, trong đó có nghề Thiết kế kỹ thuật cơ khí - CAD. Hiện đã có 1.273 thí sinh của 72 quốc gia, vùng lãnh thổ đăng ký tham dự.', '<p>Kỳ thi tay nghề thế giới lần thứ 43 sẽ được tổ chức tại Sao Paulo, Brazil từ ng&agrave;y 5 đến ng&agrave;y 17/8/2015 ở 50 nghề, trong đ&oacute; c&oacute; nghề Thiết kế&nbsp;kỹ thuật cơ kh&iacute; - CAD. Hiện đ&atilde; c&oacute; 1.273 th&iacute; sinh của 72 quốc gia, v&ugrave;ng l&atilde;nh thổ đăng k&yacute; tham dự. Đo&agrave;n Việt Nam đ&atilde; c&oacute; sự chuẩn bị kh&aacute; sớm v&agrave; đang bước v&agrave;o giai đoạn &quot;nước r&uacute;t&quot; với quyết t&acirc;m đạt th&agrave;nh t&iacute;ch cao nhất tại kỳ thi.</p>\r\n\r\n<p>Nằm trong khu&ocirc;n khổ <em>&quot;Chương tr&igrave;nh đ&agrave;o tạo của C&ocirc;ng ty Samsung d&agrave;nh cho chuy&ecirc;n gia v&agrave; th&iacute; sinh nghề thiết kế kỹ thuật cơ kh&iacute; chuẩn bị tham dự kỳ thi tay nghề thế giới lần thứ 43 tại S&atilde;o Paolo, Brazil - Nghề thiết kế kỹ thuật cơ kh&iacute; - CAD&quot;</em> do Vụ Kỹ năng nghề - Tổng cục dạy nghề v&agrave; C&ocirc;ng ty Samsung (H&agrave;n Quốc) t&agrave;i trợ tổ chức tại Trung t&acirc;m huấn luyện kỹ năng Samsung (Samsung Skills Training Center) &ndash; Samsung Digital City - Th&agrave;nh phố Suwoul &ndash; H&agrave;n Quốc, từ ng&agrave;y 01/6 đến ng&agrave;y&nbsp;15/7/2015. Trường Đại học C&ocirc;ng nghệ GTVT tham gia chương tr&igrave;nh đ&agrave;o tạo gồm c&oacute; chuy&ecirc;n gia B&ugrave;i Xu&acirc;n T&ugrave;ng &ndash; Giảng vi&ecirc;n Trung t&acirc;m C&ocirc;ng nghệ Cơ kh&iacute; &nbsp;v&agrave; Sinnh vi&ecirc;n Nguyễn Đức Th&agrave;nh.</p>\r\n\r\n<p><img alt="doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-01.jpg" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-01.jpg" style="height:499px; width:750px" /></p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c, chuy&ecirc;n gia của đo&agrave;n đ&atilde; được tiếp cận với c&aacute;c phương ph&aacute;p đ&agrave;o tạo, quy tr&igrave;nh huấn luyện th&iacute; sinh tham dự kỳ thi tay nghề thế giới; Học hỏi về luật của cuộc thi tay nghề quốc tế, t&igrave;m hiểu về c&aacute;c bản m&ocirc; tả kỹ thuật của nghề Thiết kế kỹ thuật cơ kh&iacute; - CAD; Quy tr&igrave;nh đ&aacute;nh gi&aacute; kỹ năng, cho điểm b&agrave;i l&agrave;m của th&iacute; sinh tại kỳ thi tay nghề thế giới; N&acirc;ng cao kỹ năng ra đề thi v&agrave; kiểm tra bản dịch đề thi. Th&iacute; sinh biết c&aacute;ch ph&acirc;n t&iacute;ch, l&ecirc;n kế hoạch giải quyết đề thi v&agrave; những kỹ năng sử dụng c&ocirc;ng cụ hỗ trợ trong qu&aacute; tr&igrave;nh l&agrave;m b&agrave;i thi; N&acirc;ng cao bản lĩnh l&agrave;m b&agrave;i thi; Học hỏi những kiến thức, kinh nghiệm từ th&iacute; sinh v&agrave; chuy&ecirc;n gia H&agrave;n Quốc; Được tiếp cận với m&ocirc;i trường thi đấu trến đấu trường quốc tế.</p>\r\n\r\n<p><img alt="Đoàn công tác nghề Thiết kế kỹ thuật cơ khí - CAD trường Đại học Công nghệ GTVT làm việc tại Hàn Quốc" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-02.jpg" style="height:499px; width:750px" /></p>\r\n\r\n<p>Đo&agrave;n c&ocirc;ng t&aacute;c đ&atilde; thực hiện rất tốt c&aacute;c c&ocirc;ng việc được giao tại H&agrave;n Quốc v&agrave; tất cả c&aacute;c th&agrave;nh vi&ecirc;n của đo&agrave;n được C&ocirc;ng ty Samsung cấp chứng chỉ xuất sắc. Chuyến đi đ&atilde; t&iacute;ch luỹ rất nhiều&nbsp;kinh nghiệm hữu &iacute;ch trong c&ocirc;ng t&aacute;c huấn luyện th&iacute; sinh tham dự kỳ thi tay nghề quốc tế cho Trường. B&ecirc;n cạnh đ&oacute; qua việc huấn luyện, c&aacute;c th&iacute; sinh kh&ocirc;ng những tiếp cận với c&aacute;c thiết bị, c&ocirc;ng nghệ hiện đại nhất được sử dụng tại kỳ thi thế giới sắp tới, m&agrave; c&ograve;n c&oacute; cơ hội tiếp cận với phương ph&aacute;p đ&agrave;o tạo, huấn luyện hiện đại, hiệu quả của cường quốc về c&ocirc;ng nghệ như: Đức, Mỹ, Nhật Bản, H&agrave;n Quốc...</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, với học bổng 80.000 USD m&agrave; <a href="http://utt.edu.vn/home/tin-tuc/tin-tuc-nha-truong/1070-le-trao-giai-thuong-bieu-duong-hoc-sinh-sinh-vien-tham-gia-cac-cuoc-thi-nam-hoc-2013-2014-va-phat-dong-cuoc-thi-y-tuong-sang-tao-nam-hoc-2014-2015">sinh vi&ecirc;n&nbsp;<strong>Nguyễn Đức Th&agrave;nh lớp 62CCOT04 </strong>đ&atilde; đạt được tại cuộc thi tay nghề giỏi Quốc gia năm 2013 - 2014</a>, c&ugrave;ng với qu&aacute; tr&igrave;nh học tập v&agrave; huấn luyện tại H&agrave;n Quốc trong năm vừa qua sẽ l&agrave; động lực để cho sinh vi&ecirc;n Nguyễn Đức Th&agrave;nh phấn đấu hết sức m&igrave;nh đạt th&agrave;nh t&iacute;ch cao tại kỳ thi&nbsp;tay nghề thế giới lần thứ 43 tại Brazil tới đ&acirc;y.</p>\r\n\r\n<p><img alt="Đoàn công tác nghề Thiết kế kỹ thuật cơ khí - CAD trường Đại học Công nghệ GTVT làm việc tại Hàn Quốc" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-03.jpg" style="height:499px; width:750px" /></p>\r\n\r\n<p>Sau thời gian c&ocirc;ng t&aacute;c tại H&agrave;n Quốc, Đo&agrave;n sẽ tiếp tục luyện thi cho th&iacute; sinh trong thời gian 20 ng&agrave;y ở Việt Nam (Từ ng&agrave;y 16/7 - 04/8/2015) tại Ph&ograve;ng CAD/CAM Trung t&acirc;m C&ocirc;ng nghệ&nbsp;Cơ kh&iacute; - Trường Đại học C&ocirc;ng nghệ GTVT (Số 54 Triều Kh&uacute;c - Thanh Xu&acirc;n - H&agrave; Nội - Việt Nam).&nbsp;</p>\r\n\r\n<p><img alt="Đoàn công tác nghề Thiết kế kỹ thuật cơ khí - CAD trường Đại học Công nghệ GTVT làm việc tại Hàn Quốc" src="http://utt.edu.vn/home/images/stories/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc.jpg" style="height:1057px; width:750px" /></p>\r\n\r\n<p>Hy vọng, với sự chuẩn bị chu đ&aacute;o, th&iacute; sinh Trường Đại học C&ocirc;ng nghệ GTVT (<strong><em>Nguyễn Đức Th&agrave;nh Lớp 62CDOT04</em></strong>) sẽ gi&agrave;nh được huy chương trong kỳ thi tay nghề thế giới lần thứ 43 tại Brazil mang lại vinh quang về cho Quốc gia, Li&ecirc;n Bộ GTVT&amp;Bộ LĐTB&amp;XH v&agrave; đặc biệt l&agrave; thương hiệu của Trường ĐH C&ocirc;ng nghệ GTVT.</p>\r\n\r\n<p><em>Đo&agrave;n Việt Nam dự Kỳ thi tay nghề thế giới lần thứ 43 năm 2015 ở 13 nghề: Cơ điện tử; C&ocirc;ng nghệ &ocirc; t&ocirc;; Đường ống nước; Thiết kế trang Web; X&acirc;y gạch; Mộc mỹ nghệ; H&agrave;n; Nghề thiết kế kỹ thuật cơ kh&iacute; - CAD; Giải ph&aacute;p phần mềm c&ocirc;ng nghệ th&ocirc;ng tin; Điều khiển c&ocirc;ng nghiệp; Phay CNC; Khu&ocirc;n đ&uacute;c; Kỹ thuật khu&ocirc;n đ&uacute;c nhựa.</em></p>', 1, 'vn', 1, 8, 'news', 1, 1441077419, NULL, NULL, 1),
(21, 'Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.', 'utt-ket-nap-dang-sinh-vien-07-2015-01.jpg', 'Chiều ngày 07/7/2015, lễ kết nạp đảng viên cho 5 quần chúng ưu tú là đoàn viên thanh niên, sinh viên được Chi bộ HS-SV Hà Nội tổ chức trang trọng.', '<p>Chiều ng&agrave;y 07/7/2015, lễ kết nạp đảng vi&ecirc;n cho 5 quần ch&uacute;ng ưu t&uacute; l&agrave; đo&agrave;n vi&ecirc;n thanh ni&ecirc;n, sinh vi&ecirc;n được Chi bộ HS-SV H&agrave; Nội tổ chức trang trọng.</p>\\r\\n\\r\\n<p>Tới dự, chỉ đạo v&agrave; động vi&ecirc;n tuổi trẻ, c&oacute; PGS.TS Đ&agrave;o Văn Đ&ocirc;ng - B&iacute; thư Đảng ủy, Hiệu trưởng; đồng ch&iacute; Phạm Văn T&acirc;n - Đảng ủy vi&ecirc;n, thường trực văn ph&ograve;ng Đảng ủy; đồng ch&iacute; L&ecirc; Ho&agrave;ng Anh - B&iacute; thư Đo&agrave;n trường, B&iacute; thư Chi bộ HS-SV H&agrave; Nội; c&aacute;c CB-GV-CNV đại diện GVCN-CVHT v&agrave; đ&ocirc;ng đảo sinh vi&ecirc;n quan t&acirc;m cũng tới dự, ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n trẻ.</p>\\r\\n\\r\\n<p><img alt=\\"Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.\\" src=\\"http://utt.edu.vn/home/images/stories/utt-ket-nap-dang-sinh-vien-07-2015-01.jpg\\" style=\\"display:block; height:500px; margin-left:auto; margin-right:auto; width:750px\\" /><br />\\r\\nPGS.TS Đ&agrave;o Văn Đ&ocirc;ng - B&iacute; thư Đảng ủy, Hiệu trưởng tặng hoa ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n trẻ.</p>\\r\\n\\r\\n<p>Ph&aacute;t biểu ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n trẻ, PGS.TS Đ&agrave;o Văn Đ&ocirc;ng cho biết: &ldquo;<em>Nh&agrave; trường sẽ tạo mọi điều kiện cho sinh vi&ecirc;n c&oacute; điều kiện thuận lợi nhất để học tập, r&egrave;n luyện, c&ocirc;ng t&aacute;c, phấn đấu v&agrave; trưởng th&agrave;nh. Đảng ủy, Ban Gi&aacute;m hiệu Nh&agrave; trường hy vọng v&agrave; tin tưởng rằng, trong thời gian tới sẽ c&oacute; nhiều sinh vi&ecirc;n ưu t&uacute; được kết nạp v&agrave;o Đảng C&ocirc;ng sản Việt Nam</em>&hellip;&rdquo;.</p>\\r\\n\\r\\n<p><img alt=\\"Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.\\" src=\\"http://utt.edu.vn/home/images/stories/utt-ket-nap-dang-sinh-vien-07-2015-05.jpg\\" style=\\"display:block; height:500px; margin-left:auto; margin-right:auto; width:750px\\" /><br />\\r\\n<em>C&ocirc;ng bố v&agrave; trao QĐ kết nạp đảng vi&ecirc;n cho đồng ch&iacute; Phạm C&ocirc;ng T&uacute; - 63DCCD07</em></p>\\r\\n\\r\\n<p><img alt=\\"Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.\\" src=\\"http://utt.edu.vn/home/images/stories/utt-ket-nap-dang-sinh-vien-07-2015-02.jpg\\" style=\\"height:500px; width:750px\\" /></p>\\r\\n\\r\\n<p><em>C&ocirc;ng bố v&agrave; trao QĐ kết nạp đảng vi&ecirc;n cho đồng ch&iacute; Vũ Thị Trang - 63CCKK01</em><br />\\r\\n<br />\\r\\n<img alt=\\"Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.\\" src=\\"http://utt.edu.vn/home/images/stories/utt-ket-nap-dang-sinh-vien-07-2015-03.jpg\\" style=\\"height:500px; width:750px\\" /></p>\\r\\n\\r\\n<p><em>C&ocirc;ng bố v&agrave; trao QĐ kết nạp đảng vi&ecirc;n cho đồng ch&iacute; Nguyễn Ph&uacute; Tuấn Anh &ndash; 63DCKT07</em><br />\\r\\n<br />\\r\\n<img alt=\\"Chúc mừng năm sinh viên ưu tú được vinh dự đứng trong hàng ngũ của Đảng.\\" src=\\"http://utt.edu.vn/home/images/stories/utt-ket-nap-dang-sinh-vien-07-2015-04.jpg\\" style=\\"height:500px; width:750px\\" /></p>\\r\\n\\r\\n<p><em>C&ocirc;ng bố v&agrave; trao QĐ kết nạp đảng vi&ecirc;n cho đồng ch&iacute; L&ecirc; Thị V&acirc;n Anh &ndash; 63CCKK01 (tr&aacute;i) v&agrave; đồng ch&iacute; Ho&agrave;ng Anh Tuấn &ndash; 64DLDD01 (phải)</em></p>\\r\\n\\r\\n<p>C&aacute;c đảng vi&ecirc;n trẻ x&uacute;c động chia sẻ: &ldquo;<em>Được kết nạp v&agrave;o Đảng Cộng sản Việt Nam l&agrave; vinh dự, tự h&agrave;o đồng thời cũng gắn với &yacute; thức tr&aacute;ch nhiệm c&agrave;ng lớn hơn. Ch&uacute;ng t&ocirc;i sẽ tiếp tục r&egrave;n luyện n&acirc;ng cao nhận thức ch&iacute;nh trị, đạo đức lối sống; lu&ocirc;n chủ động trong học tập, nghi&ecirc;n cứu khoa học đồng thời t&iacute;ch cực r&egrave;n luyện t&aacute;c phong th&ocirc;ng qua c&ocirc;ng t&aacute;c Đo&agrave;n - Hội, phong tr&agrave;o thanh ni&ecirc;n, sinh vi&ecirc;n&hellip;, trở th&agrave;nh người c&oacute; &iacute;ch cho gia đ&igrave;nh, x&atilde; hội</em>&rdquo;.</p>', 1, 'vn', 1, 8, 'news', 50, 1441077465, 1441212838, NULL, 1),
(22, 'Thông báo các cơ quan, đơn vị xem phim tài liệu về Ngành GTVT', '872cd49e677a325aeca0e161100d57c9_w250_h170_cp.jpg', 'Nằm trong chuỗi hoạt động kỷ niệm 70 năm ngày truyền thống ngành Giao thông vận tải (28/8/1945 - 28/8/2015) và Đại hội thi đua yêu nước ngành Giao thông vận tải (2015 - 2020), Bộ Giao thông vận tải và Đài truyền hình Việt Nam đã phối hợp thực hiện Bộ phim tài liệu \\"70 năm Giao thông vận tải - đi trước mở đường\\".', '<div>&nbsp;</div>\\r\\n\\r\\n<p><img alt=\\"70 năm Giao thông vận tải - đi trước mở đường\\" src=\\"http://utt.edu.vn/home/images/stories/thong-bao-cac-co-quan-don-vi-xem-phim-tai-lieu-ve-nganh-gtvt.jpg\\" style=\\"height:990px; width:750px\\" /></p>', 1, 'vn', 1, 8, 'news', 5, 1441077584, 1441415624, NULL, 1),
(129, 'The history and tradition of the University of Technology of Transport', '35624f0422ac91dae9a19405f96ff986_w250_h170_cp2.jpg', 'University of Technology of Transport is a public university, under the Ministry of Transport. School history developed over time.', '<p>University of Technology of Transport is a public university, under the Ministry of Transport. School history developed over time.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; BRIEF HISTORY OF COLLEGE of Transportation before 1945<br />\r\n<br />\r\nFrench colonial period from 1945 and earlier in Vietnam had the College of Public Works since 1902. Summary development training school system transportation personnel as follows:<br />\r\n<br />\r\n- 06/6/1902 open Clerical School and specialization Public officials in Hanoi.<br />\r\n- 04/15/1913 Renamed primary Schools in Hanoi. Vocational Training Professional worker justice, training time: 2 years<br />\r\n<br />\r\n- 1918 lifting up schools: College of primary but secondary education is the primary technique, until the year 1923 to 1924 have recruited some justice officials completed 1 year to learn the engineer (class 4 person)<br />\r\n<br />\r\n- 1925: Staff training specialization natives (the French, who was born in the South, Hanoi, Hai Phong, Da Nang is considered French citizens) for the establishment of justice, administration, time training time increased to 3 years in this period, in 1925-1926 open class engineer for 5 people.<br />\r\n<br />\r\n- Schools temporarily suspend the students in 1931-1938 school year as demand from HR.<br />\r\n<br />\r\n- 8/1938 School reactivated back still training righteousness, 3-year training period, for the course: 1938-1941, 1939-1942, 1941-1944, and 1942-1944 school course is 2 years By early January, the Japanese main islands France 3/1945, school closures, school suspension, the student returned home. Later some finishing school back in the College of Public Works under the revolutionary government, or under the puppet regime established in 1947 in Saigon.<br />\r\n<br />\r\n- In 1943-1944 students choose three courses ranked first from 1938 to 1941, five students have learning outcomes achieved 13 points or more courses from 1939 to 1942, to the first class engineers, 10-month study period (while Nguyen Nhat Quang classes SV and SV Vu Duc Kidney School Principal of the latter). 1944 American B24 bomber plane bases of Japanese troops moved up the class engineers in Da Lat and month new 9/1944 graduation.<br />\r\n<br />\r\n- While training time over 40 years (from 6/1902 to 3/1945 month) but the amount of training is very little, according to the senior class ahead of the amount of training from 1902 to 1945 have systems 13 course is 204 people, while 2 class engineer (1923 and 1925-1926) is 9 people and one engineer class in 1944 was 8 the rest was rolled righteousness: 187 people.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; COLLEGE OF THE OPENING AND TRAINING FROM 1945 - 1957<br />\r\n<br />\r\nIn August 1945, under the leadership of the Party and the leader Ho Chi Minh, our people have gained independence, established the State of Vietnam Democratic State Hoa- agricultural community first in Southeast Asia. In terms still many difficulties, before the situation in enemy, but the enemy outside the Central Party and President Ho Chi Minh was interested in &quot;killing illiteracy&quot;, paving the way for the training and retraining of staff to build the country, a new culture is set according to three principles: &quot;Ethnic cultural, scientific, and popularization of&quot;.<br />\r\n<br />\r\nTransportation sector is the lifeblood of the national economy, have very important positions, involving all aspects of the economy, society and national defense. The training of technical staff for the Transport sector become urgent requirement and essential at the time. Implementing the policy of the Party and State, College of Public Administration- forerunner of the College of Transport dated That represented a re-opening was November 15, 1945 under the Decree of the Minister of National Education Vu Dinh Hoe and the Minister of Transport and Public Works Dao Trong Kim. School&#39;s headquarters are located at 33 Hang Tre. Nguyen Nhu Foundation, ranks fourth engineer in chief, Director Dental Railway tasked School Principals.<br />\r\n<br />\r\nMay 12/1946, French colonists returned to invade our country. Booming national resistance. College of the suspension of training, evacuated and serve resistance.<br />\r\n<br />\r\nMay 10/1947, where evacuation (Vien Dinh Pagoda, the village of Cong Than- Chuom Great Market, Phu Xuyen district, Ha Dong) has officially opened its first session of the College of Public Works consists of 3 layers (2 college class, class 1 Middle class).<br />\r\n<br />\r\nFrench colonial expansion plot occupied areas, endangering Ha Dong. May 4/1948 Schools must move in Yen Mo district, Ninh Binh province. Dang Phuc Thong, Deputy Minister of Transport and Public Works directly as Rector. Till July 1949, the school organized a national examination for two key 1 college and college system. Following 17sinh graduates, while 7 students College Engineering, 10 students graduated from college.<br />\r\n<br />\r\nIn February 1949, according to Ordinance No. 2 SL, 01 May 02 1949 of the Government on the establishment of the Technical College and Decree No 60-D / SH 24 May 02 1949 of the Ministry of Communications Main, the school was renamed the College of Engineering, is responsible for training of technical staff in public traffic, architecture, post office ... to meet the requirements for production, fought in the resistance war against the French colonialists.<br />\r\n<br />\r\nEarly in 1949, the Party&#39;s first school was founded, establishing the Party&#39;s leadership role for the activities of the university.<br />\r\n<br />\r\nMay 2/1951, the school continued to be evacuated in Da Ne Yen Dinh Phong Thanh Hoa Lac and converted, to Mai Bridge, Central Government, Tho Xuan district. Despite moving parts, ensuring school teaching, learning, ensure the activities of the university; conducting enrollment and course 2 course 3, including two training courses both intermediate colleges and justice, of training course, 3 Public middle class, middle-class mail, the training period is 2 years.<br />\r\n<br />\r\nIn 1950 the university held graduation exam Monday for 2 College class IIA, II-B and a class Middle class II course; 26 college students, 14 Middle class graders were Minister of Transport and Public Works Tran Dang Khoa graduated recognized in decree 275-SH / ND.<br />\r\n<br />\r\n1951, to ensure safety and to enlist the leaders of the Central Government and the whole school to evacuate to North Vietnam. By the end of 1951, the school moved to the village of Chau, Yen Son District, Tuyen Quang and played there until the restoration of peace in 1954.<br />\r\n<br />\r\nDuring this time, the university while ensuring learning for 3 courses, course 4, held graduation for 30 college students 3 courses; concurrent enrollment courses 5 and 6, bringing the total number of students to 200, not including postal Intermediate layer 4 was separated keys to establish Intermediate School Postal required by development of the postal service. In addition the school also organizes training of elementary knowledge about bridges than 100 cadres and workers, the youth volunteers.<br />\r\n<br />\r\nPeace was restored, prior request and new tasks of the revolution, over 100 teachers and school organizations established survey team consisting of six teams under the command of Mr. Nguyen Nhat Quang, Rector, respondents police, railway design Nam Quan Hanoi-section. Also from January 3/1955, the Ministry of Transport and Public Works has decided to withdraw a number of school staff (Mr. Nguyen Nhat Quang, Dao Huu Beard, Nguyen Thanh Van) to the establishment of a new school Rebuilding. The head office of the board is located at 33 Nguyen Thuong Hien Street, then moved to the village of Thu Le (on Elephant for current zone) to carry out the work to rebuild the school.<br />\r\n<br />\r\nAfter completion of the mission to restore the railway line, staff, teachers and students return to school in Hanoi build new premises in Bridges paper to reopen the College of Public Works and admissions preparation course Intermediate 7, was opened on July 6, 1955.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; PERIOD SCHOOL NAME SCHOOL WEAR Transportation - (1957 - 1996)<br />\r\n<br />\r\nMay 8/1956, the State has decided to move to the workforce training large intermediate level to serve the country in the building of peace, the College of Public Works were separated into Intermediate School: Intermediate Traffic, Intermediate and Intermediate Hydraulic Structures, these institutions started operation in early 1957. Traffic School of continued enrollment training courses 3 years 8 systems including industries: bridge, road, head rolling stock, trade away (rail transportation). The number of students and courses 8 key 7 are employed around 300 people. Next keyword 9 to 12 courses, each course enrollment of 350 students in school. Every year for about 300 officers graduating technical colleges have good quality contributes significantly to the development of the technical staff for the transport sector, serving restore blood vessel traffic after the restoration of peace in South uncle.<br />\r\n<br />\r\nMay 8/1960, the Ministry of Transport has decided to establish the building Transport University, where Mr. Nguyen Nhat Quang and some parts of leading cadres, teachers and workers of Secondary School Transport was transferred to work at the University of transportation.<br />\r\n<br />\r\nImplementation of Resolution of the Third National Party (1960), after training to 12 courses, the School of traffic separated into 2 School: School Transportation Resources - Ministry and School Affairs Railway Transport.<br />\r\n<br />\r\n- School Transport Railway Bridge closed at Voi for paper. By the end of 1964 by the American invaders attacking the North, School of Social evacuated to commune and Dong Van district director of Tam Dao, Vinh Phuc. Economic Sciences in 1968 from the School of detached established School of Economic and until 1971 to enter the School of Economics at the School of Railway Transport.<br />\r\n<br />\r\n- High School Department of Transportation Resources, initially closed at Voi for Iron Paper, to switch construction 1963 Eternal in Lang (now the University of Transport KTX on Nguyen Chi Thanh). Late in 1964 stationed in evacuation on Heron Lake (now Dong Tommy Ward, Vinh Yen Town, Vinh Phuc).<br />\r\n<br />\r\n- 1970 School of Fisheries Department of Transportation split into two schools: School of Transport Roads and Transport Waterway Primary School.<br />\r\n<br />\r\n- School Transport moved to close the waterway in Thai and An Tinh Van commune, Cam Giang District, Hai Duong Province. 1974 to build permanent transfer in Advanced commune, Thanh Ha. Ocean. 1975 accepting Hydro Engineering Science High School (machines, housings ships) Maritime University School merged. In late 1975, after reunification, a department of the University academic staff are appointed to the Ho Chi Minh City as the framework for the establishment of Colleges of Transportation 6 (now City College of Transport III. Lake Chi Ming).<br />\r\n<br />\r\n- Transport School in Heron Lake Road closed, but the years of the American war to evacuate the surrounding districts to ensure that teaching and learning.<br />\r\n<br />\r\nThe end of 1975 High School Road Traffic and Transportation Railway Secondary chose appoint some school leaders and staff of departments, faculty and staff with experience in Phu Tai, Binh Dinh founded schools UT 5 (now College of Transport II, stationed in Da Nang), and send teachers to support for the School of Transportation 6 (TP. HCM)<br />\r\n<br />\r\nSince 1970 4 1977 Transportation Road Primary School in collaboration with the Ministry of Transport School sent a number of officers and experienced teacher to make expert help Laos established the Central School of traffic and direct Laos You teach water official site training of technical staff in service protecting and building the country of Laos.<br />\r\n<br />\r\n- May 12/1983 MOT for 3 School merger of Transportation: Road, Sea Road, Rail Transportation Secondary School of I and transfer the entire infrastructure, management staff and local teachers Transport of Road Primary School (Dam cauldron - Vinh Yen). In the 1980s, in addition to human resource training and technical labor for reunification, the university also sent some experienced teachers as experts to help Cambodia to organize training of technical personnel transport carriage.<br />\r\n<br />\r\n- On 02.08.1990, the Ministry of Transport has decided to merge School Taiyuan Mountain Transport (based in Tan Thinh, Ho. Thai Nguyen, Thai Nguyen) with the first of Transport School Transport School region I. Then the agency remains in effect Dam cauldron - Vinh Yen, Vinh Phuc, Thai Nguyen also be converted at the Training Center of the School of Transport Mountain Transportation KV TH I<br />\r\n<br />\r\n- On 30.10.1991, the Ministry of Transport has decided to merge Schools Hanoi Car mechanic (mechanical Traffic Department) in School Transport Zone I and renamed branch of the School of Vocational Training , Since 1993 the main campus of the School of Transportation KVI placed in Hanoi on the location of this branch of vocational training.<br />\r\n<br />\r\n- Thus the period from 1960 to 1996 and especially from 1983 to 1991 was a period of 4 to enter the Northern Transportation School and Vocational School in Hanoi mechanic, a School Transportation KVI, is a large professional secondary schools in Vietnam.<br />\r\n<br />\r\n- From 1960 to 1996 the school had 35 recruits training course (keyword key 13 to 47) with over 5 thousand student system and Vocational THCN technical workers for Transport Sector, serve effectively pave the way for Liberation of South south and nation-building, after the two sides agreed and the early economic and social renewal. During the renovation period the school has expanded to diversify the types of training, in addition to more specialized training THCN systems, systems and school Job Vocational also expanding the types of training such as: training pilots College undergraduate point XD bridge (from 1992) and Mechanical Engineering automobile-building machines (from 1993), secondary systems and applications Computing Vocational Technical inspection of road quality, etc.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; PERIOD OF COLLEGES OF TRANSPORT<br />\r\n<br />\r\nOn 07/24/1996, the Prime Minister signed Decision No. 480 / TTg upgrades Transportation School Region I of the College of transportation. Since then THCN and external training system Vocational training schools BA technical colleges and the economy. Besides regular training focused also expanded training forms as: Regular address (for the Corporation in the transport sector), In-service training college accreditation from THCN to colleges and affiliated with the University of in-service training and special training graduate from college to the University for a number of specialized training.<br />\r\n<br />\r\nFor College system, the early pilot training in Hanoi, then expand to the second training base in Vinh Yen and Thai Nguyen. From 1998 to facilitate 2 High School and Middle School Transport Transport KVII III preparation facilities and qualified teaching staff, school open joint training college with 2 cases in the city. Danang and HCMC. Ho Chi Minh City until 2 school was upgraded to a college.<br />\r\n<br />\r\nSince 1997, the College of Transport continue training recruits are 9 key (48 keys to 56 keys), since 2000 the school enrollment quota allocated. year higher than last year, the average annual recruitment in 3600 HS-SV (including colleges in 1500, THCN 1500, Vocational 600). In addition to formal training focused on careers, training school continues short drive motorcars and motorcycles 2 wheels; regularly attend classes managing professional training for officers of the State in Transportation Sector.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; PERIOD UNIVERSITY OF TECHNOLOGY TRANSPORT (UTT)<br />\r\n<br />\r\nOn 27/04/2011, the Prime Minister signed Decision No. 630 / QD-TTg on the establishment of the University of Technology of Transportation (University Of Transport Technology) based on upgrading the College of Transport. Currently, the school has three training facilities: Facility 1 at No. 54 North Street, Thanh Xuan Nam Khuc- Ward, Thanh Xuan District, Hanoi. Ha Noi; Facility 2 at Dong Tam Ward, Vinh Yen Town, Vinh Phuc Province and Facility 3 at Tan Thinh, Ho. Thai Nguyen, Thai Nguyen Province.<br />\r\n<br />\r\nHS-annual flow of nearly 15 thousand students. Expected training scale of 20,000 HS-SV School in 2015. Personnel teachers and employees at present about 652 people, including 454 faculty people (30 Ph.D. and 310 Masters NCS; university 114)</p>', 1, 'en', 3, 8, 'news', 88, 1441416119, 1441417318, NULL, 1),
(23, 'Thông báo phân công Giáo viên chủ nhiệm - Cố vấn học tập cho các lớp khoá 66', '47bf2acacddbe4602a47d11d9532c0ec_w250_h170_cp4.jpg', 'Thực hiện Quy định Giáo viên chủ nhiệm - Cố vấn học tập của Trường Đại học Công nghệ GTVT ban hành kèm theo Quyết định số 1816/QĐ-ĐHCNGTVT-CTHSSV ngày 12/8/2013 của Hiệu trưởng Trường Đại học Công nghệ GTVT, đề nghị các Khoa phân công Giáo viên chủ nhiệm - Cố vấn học tập cho các lớp khóa 66 (có danh sách lớp kèm theo), ưu tiên giảng viên chuyên ngành trước (kể cả giảng viên kiêm nhiệm) sau đó đến giảng viên ở các khoa Cơ sở kỹ thuật, khoa Khoa học cơ bản, khoa Lý luận chính trị.', '<p>Thực hiện Quy định Gi&aacute;o vi&ecirc;n chủ nhiệm - Cố vấn học tập của Trường Đại học C&ocirc;ng nghệ GTVT ban h&agrave;nh k&egrave;m theo Quyết định số 1816/QĐ-ĐHCNGTVT-CTHSSV ng&agrave;y 12/8/2013 của Hiệu trưởng Trường Đại học C&ocirc;ng nghệ GTVT, đề nghị c&aacute;c Khoa ph&acirc;n c&ocirc;ng Gi&aacute;o vi&ecirc;n chủ nhiệm - Cố vấn học tập cho c&aacute;c lớp kh&oacute;a 66 (c&oacute; danh s&aacute;ch lớp k&egrave;m theo), ưu ti&ecirc;n giảng vi&ecirc;n chuy&ecirc;n ng&agrave;nh trước (kể cả giảng vi&ecirc;n ki&ecirc;m nhiệm) sau đ&oacute; đến giảng vi&ecirc;n ở c&aacute;c khoa Cơ sở kỹ thuật, khoa Khoa học cơ bản, khoa L&yacute; luận ch&iacute;nh trị.</p>\\r\\n\\r\\n<p>C&aacute;c Giảng vi&ecirc;n đ&atilde; tham gia c&ocirc;ng t&aacute;c GVCN - CVHT đối với c&aacute;c kh&oacute;a kh&aacute;c đang theo học tại Trường th&igrave; kh&ocirc;ng tham gia c&ocirc;ng t&aacute;c n&agrave;y đối với kh&oacute;a 66, đảm bảo mỗi giảng vi&ecirc;n chỉ chủ nhiệm một lớp sinh vi&ecirc;n. Danh s&aacute;ch gửi về c&aacute;c Khoa chuy&ecirc;n ng&agrave;nh, c&aacute;c Khoa chuy&ecirc;n ng&agrave;nh tập hợp, ph&acirc;n c&ocirc;ng v&agrave; gửi về ph&ograve;ng C&ocirc;ng t&aacute;c HSSV (P.102H3) chậm nhất đến hết ng&agrave;y 31/8/2015.</p>\\r\\n\\r\\n<p>FILE: Demo</p>', 1, 'vn', 1, 8, 'news', 2, 1441077656, 1441415572, NULL, 1);
INSERT INTO `utt_post` (`id`, `title`, `image`, `description`, `detail`, `site_id`, `lang`, `status`, `user_id`, `post_type`, `cate_id`, `time_create`, `time_update`, `sort`, `is_top`) VALUES
(24, 'Thông báo Phân công nhiệm vụ xây dựng và báo cáo các nội dung giảng dạy trong tuần sinh hoạt công dân đầu khoá đối với các lớp sinh viên khoá 66', 'e8d466f81c6fabf2935404464949fba8_w250_h170_cp2.jpg', 'Thực hiện Kế hoạch số 1989/KH-ĐHCNGTVT ngày 19 tháng 8 năm 2015 của Hiệu trưởng trường Đại học Công nghệ GTVT về việc tổ chức “Tuần sinh hoạt công dân - HSSV” năm học 2015 - 2016.', '<p>Thực hiện Kế hoạch số 1989/KH-ĐHCNGTVT ng&agrave;y 19 th&aacute;ng 8 năm 2015 của Hiệu trưởng trường Đại học C&ocirc;ng nghệ GTVT về việc tổ chức <em>&ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n - HSSV&rdquo;</em> năm học 2015 - 2016. Ban Gi&aacute;m Hiệu ph&acirc;n c&ocirc;ng nhiệm vụ cho c&aacute;c đơn vị x&acirc;y dựng v&agrave; b&aacute;o c&aacute;o c&aacute;c nội dung giảng dạy trong tuần sinh hoạt c&ocirc;ng d&acirc;n đầu kh&oacute;a đối với c&aacute;c lớp sinh vi&ecirc;n kh&oacute;a 66, như sau:</p>\\r\\n\\r\\n<div>\\r\\n<table border=\\"2\\" cellpadding=\\"1\\" style=\\"height:986px; width:1040px\\">\\r\\n	<tbody>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<hr />\\r\\n			<p><strong>Nội dung giảng dạy</strong></p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>Đơn vị phụ tr&aacute;ch</strong></p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>Thời gian</strong></p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>1</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Qu&aacute;n triệt phương hướng nhiệm vụ của Đảng bộ Trường nhiệm kỳ 2015 - 2020; th&ocirc;ng tin về t&igrave;nh h&igrave;nh kinh tế, ch&iacute;nh trị, x&atilde; hội trong nước, khu vực v&agrave; quốc tế; nội dung c&ocirc;ng t&aacute;c trọng t&acirc;m của Trường năm học 2015 - 2016; chiến lược ph&aacute;t triển Trường đến năm 2020 v&agrave; tầm nh&igrave;n đến năm 2030; gi&aacute;o dục truyền thống nh&agrave; trường hướng tới kỷ niệm 70 năm th&agrave;nh lập Trường.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ban Gi&aacute;m hiệu</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>01 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>2</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Giới thiệu chung về cấu tr&uacute;c CTĐT; chuẩn đầu ra; kế hoạch đ&agrave;o tạo năm học 2015-2016; li&ecirc;n th&ocirc;ng giữa c&aacute;c ng&agrave;nh đ&agrave;o tạo; Quy chế đ&agrave;o tạo theo t&iacute;n chỉ v&agrave; c&aacute;c quy định về đ&agrave;o tạo của Trường; giới thiệu v&agrave; hướng dẫn sử dụng chương tr&igrave;nh quản l&yacute; đ&agrave;o tạo đối với sinh vi&ecirc;n đăng k&yacute; học phần, tra cứu v&agrave; khai th&aacute;c th&ocirc;ng tin tr&ecirc;n mạng.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ph&ograve;ng Đ&agrave;o tạo</p>\\r\\n\\r\\n			<p>Ph&ograve;ng ĐBCLĐT</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>02 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>3</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Quy chế HSSV; Quy chế điểm r&egrave;n luyện; Quy định c&aacute;n bộ lớp; hướng dẫn khai b&aacute;o hồ sơ tr&ecirc;n chương tr&igrave;nh quản l&yacute; đ&agrave;o tạo; quy định thi sinh vi&ecirc;n giỏi; sinh vi&ecirc;n NCKH v&agrave; c&aacute;c quy định li&ecirc;n quan đến sinh vi&ecirc;n; học bổng KKHT, học ph&iacute;; Chế độ ch&iacute;nh s&aacute;ch, BHYT, BHTT, vay vốn t&iacute;n dụng; Quy tr&igrave;nh h&agrave;nh ch&iacute;nh một cửa; c&ocirc;ng t&aacute;c GVCN - CVHT.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ph&ograve;ng CTHSSV</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>02 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>4</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Giới thiệu nhu cầu nguồn nh&acirc;n lực ng&agrave;nh đ&agrave;o tạo; y&ecirc;u cầu chuẩn đầu ra đối với chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo v&agrave; chi tiết nội dung chương tr&igrave;nh đ&agrave;o tạo; điều kiện đội ngũ giảng vi&ecirc;n, cơ sở vật chất, trang thiết bị phục vụ đ&agrave;o tạo, NCKH; phương ph&aacute;p học tập, kế hoạch học tập cụ thể học kỳ 1 cho sinh vi&ecirc;n thuộc Khoa chuy&ecirc;n ng&agrave;nh quản l&yacute;.&nbsp;</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>C&aacute;c Khoa</p>\\r\\n\\r\\n			<p>chuy&ecirc;n m&ocirc;n</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>02 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>5</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Giới thiệu c&aacute;c chương tr&igrave;nh đ&agrave;o tạo bằng tiếng nước ngo&agrave;i; chương tr&igrave;nh dự bị đại học v&agrave; thạc sĩ tại Ph&aacute;p</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ph&ograve;ng KHCN -HTQT</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>1/3 buổi</p>\\r\\n\\r\\n			<p>&nbsp;</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>6</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Giới thiệu Trung t&acirc;m th&ocirc;ng tin thư viện, nguồn t&agrave;i liệu phục vụ học tập, nghi&ecirc;n cứu cho sinh vi&ecirc;n.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Thư viện</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>1/3 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>7</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Phương thức nộp học ph&iacute;, c&aacute;c khoản thu kh&aacute;c (nếu c&oacute;)</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ph&ograve;ng TCKT</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>1/3 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>8</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Chương tr&igrave;nh c&ocirc;ng t&aacute;c của Đo&agrave;n Thanh ni&ecirc;n CSHCM nh&agrave; trường năm học 2015 - 2016; c&aacute;c hoạt động ngoại kh&oacute;a; c&aacute;c c&acirc;u lạc bộ sinh vi&ecirc;n.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>ĐTN</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>01 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>9</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Giao lưu thể thao, VHVN.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Tổ TDTT</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>03 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>10</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>GVCN - CVHT sinh hoạt với lớp sinh vi&ecirc;n.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>GVCN</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>01 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>11</p>\\r\\n			</td>\\r\\n			<td style=\\"vertical-align:middle\\">\\r\\n			<p>Đ&aacute;nh gi&aacute; kết quả đợt học tập tuần sinh hoạt c&ocirc;ng d&acirc;n của sinh vi&ecirc;n.</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Ph&ograve;ng CTHHSSV v&agrave; c&aacute;c đơn vị</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>02 buổi</p>\\r\\n			</td>\\r\\n		</tr>\\r\\n	</tbody>\\r\\n</table>\\r\\n\\r\\n<p>Căn cứ c&aacute;c nội dung được ph&acirc;n c&ocirc;ng, Nh&agrave; trường y&ecirc;u cầu l&atilde;nh đạo c&aacute;c đơn vị chỉ đạo x&acirc;y dựng v&agrave; cử giảng vi&ecirc;n b&aacute;o c&aacute;o c&aacute;c nội dung trong tuần sinh hoạt c&ocirc;ng d&acirc;n đầu kh&oacute;a cho sinh vi&ecirc;n kh&oacute;a 66. Nội dung b&aacute;o c&aacute;o được tr&igrave;nh b&agrave;y bằng Powerpoint v&agrave; gửi về Ban Gi&aacute;m Hiệu duyệt th&ocirc;ng qua ph&ograve;ng C&ocirc;ng t&aacute;c HSSV trước ng&agrave;y 03/9/2015. Ph&ograve;ng C&ocirc;ng t&aacute;c HSSV lập thời kh&oacute;a biểu gửi c&aacute;c đơn vị trước ng&agrave;y 03/9/2015.</p>\\r\\n</div>', 1, 'vn', 1, 8, 'news', 2, 1441077840, 1441415554, NULL, 1),
(25, 'Kế hoạch Tổ chức Tuần sinh hoạt công dân - học sinh, sinh viên năm học 2015 - 2016', '47bf2acacddbe4602a47d11d9532c0ec_w250_h170_cp3.jpg', 'Kế hoạch Tổ chức Tuần sinh hoạt công dân - học sinh, sinh viên năm học 2015 - 2016', '<p><strong>I. MỤC Đ&Iacute;CH, Y&Ecirc;U CẦU</strong></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>1. &nbsp;Mục đ&iacute;ch</strong></em></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>- N&acirc;ng cao nhận thức của sinh vi&ecirc;n về đường lối, chủ trương của Đảng, ch&iacute;nh s&aacute;ch Ph&aacute;p luật của Nh&agrave; nước; gi&uacute;p cho sinh vi&ecirc;n nắm được t&igrave;nh h&igrave;nh kinh tế, ch&iacute;nh trị, x&atilde; hội trong nước, khu vực v&agrave; quốc tế.<br />\\r\\n\\\\r\\\\n- Gi&uacute;p HSSV hiểu r&otilde; quyền lợi, nghĩa vụ, &yacute; thức tr&aacute;ch nhiệm của c&ocirc;ng d&acirc;n &ndash; HSSV th&ocirc;ng qua việc tuy&ecirc;n truyền, phổ biến ph&aacute;p luật, c&aacute;c quy chế, quy định về đ&agrave;o tạo, về c&ocirc;ng t&aacute;c HSSV của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; của Nh&agrave; trường; phương hướng nhiệm vụ năm học 2015 &ndash; 2016 của Trường; nhiệm vụ của người học nhằm đ&aacute;p ứng y&ecirc;u cầu đ&agrave;o tạo, r&egrave;n luyện v&agrave; nhu cầu của x&atilde; hội.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>2. Y&ecirc;u cầu</strong></em></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>Việc tổ chức <em>&ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n &ndash; HSSV&rdquo;</em> đầu năm học 2015 &ndash; 2016 phải được tiến h&agrave;nh nghi&ecirc;m t&uacute;c, đạt hiệu quả gi&aacute;o dục cao; c&oacute; thảo luận, đối thoại, viết thu hoạch v&agrave; đ&aacute;nh gi&aacute; kết quả cuối đợt học tập.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><strong>II. NỘI DUNG</strong>&nbsp;</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>1. C&ocirc;ng tác giáo dục chính trị tư tưởng</strong></em></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>- Qu&aacute;n triệt nội dung cơ bản Nghị quyết số 29 - NQ/TƯ ng&agrave;y 4/11/2013 của Hội nghị lần thứ 8 Ban chấp h&agrave;nh Trung ương kh&oacute;a XI v&agrave; Nghị quyết số 44/NQ - CP của Ch&iacute;nh phủ ban h&agrave;nh Chương tr&igrave;nh h&agrave;nh động của Ch&iacute;nh phủ thực hiện Nghị quyết số 29 -NQ/TƯ ng&agrave;y 4/11/2013 của Hội nghị lần thứ 8 Ban chấp h&agrave;nh Trung ương kh&oacute;a XI về <em>&ldquo;Đổi mới căn bản, to&agrave;n diện gi&aacute;o dục đ&agrave;o tạo, đ&aacute;p ứng nhu cầu c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a trong điều kiện kinh tế thị trường định hướng x&atilde; hội chủ nghĩa v&agrave; hội nhập quốc tế&rdquo;</em> ph&ugrave; hợp với nhiệm vụ của Trường.<br />\\r\\n- Qu&aacute;n triệt phương hướng nhiệm vụ của Đảng bộ Trường; th&ocirc;ng tin về t&igrave;nh h&igrave;nh kinh tế, ch&iacute;nh trị, x&atilde; hội trong nước, khu vực v&agrave; Quốc tế thời gian qua; một số nội dung c&ocirc;ng t&aacute;c trọng t&acirc;m của nh&agrave; trường năm học 2015 &ndash; 2016 hướng tới kỷ niệm 70 năm th&agrave;nh lập Trường.<br />\\r\\n- Tiếp tục đẩy mạnh học tập v&agrave; l&agrave;m theo tư tưởng, tấm gương đạo đức, phong c&aacute;ch Hồ Ch&iacute; Minh; thực hiện chủ đề của năm học 2014 - 2015: <em>&ldquo;Học tập v&agrave; l&agrave;m theo tấm gương đạo đức Hồ Ch&iacute; Minh về n&ecirc;u cao tinh thần tr&aacute;ch nhiệm, chống chủ nghĩa c&aacute; nh&acirc;n, n&oacute;i đi đ&ocirc;i với l&agrave;m&rdquo;</em>.<br />\\r\\n- Tổ chức gi&aacute;o dục chuẩn mực đạo đức nghề nghiệp v&agrave; đạo đức c&ocirc;ng vụ cho sinh vi&ecirc;n ph&ugrave; hợp ng&agrave;nh đ&agrave;o tạo v&agrave; gắn với tr&aacute;ch nhiệm của sinh vi&ecirc;n trong giai đoạn hiện nay; Chiến lược ph&aacute;t triển gi&aacute;o dục 2011 - 2020 của Ngành giáo dục và của Nh&agrave; trường.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>2. C&ocirc;ng t&aacute;c HSSV</strong></em></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>- Giới thiệu Chương tr&igrave;nh C&ocirc;ng t&aacute;c HSSV của Trường năm học 2015-2016.<br />\\r\\n- C&aacute;c Quy chế, quy định về đ&agrave;o tạo, về c&ocirc;ng t&aacute;c HSSV của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, Bộ Lao động - Thương binh v&agrave; X&atilde; hội v&agrave; c&aacute;c nội dung do Nh&agrave; trường quy định; cấu tr&uacute;c, nội dung chương tr&igrave;nh đ&agrave;o tạo; chuẩn đầu ra; c&aacute;c quy tr&igrave;nh, thủ tục một cửa đối với sinh vi&ecirc;n; chế độ, ch&iacute;nh s&aacute;ch, học bổng KKHT; th&ocirc;ng tin về chủ trương t&iacute;n dụng cho sinh vi&ecirc;n; giới thiệu nhu cầu nguồn nh&acirc;n lực của ng&agrave;nh GTVT v&agrave; x&atilde; hội; c&aacute;c hoạt động văn ho&aacute;, văn nghệ, thể thao; c&ocirc;ng t&aacute;c y tế trường học, &yacute; nghĩa v&agrave; tr&aacute;ch nhiệm của việc tham gia bảo hiểm y tế bắt buộc v&agrave; tham gia BHTT tự nguyện.<br />\\r\\n- C&aacute;c nội dung, y&ecirc;u cầu trong đ&agrave;o tạo theo t&iacute;n chỉ v&agrave; học tập đ&aacute;p ứng nhu cầu của x&atilde; hội.<br />\\r\\n- Giới thiệu những nội dung li&ecirc;n quan tới người học được đề cập trong Luật Gi&aacute;o dục năm 2005 v&agrave; Luật sửa đổi, bổ sung một số điều của Luật Gi&aacute;o dục năm 2009; Luật gi&aacute;o dục đại học năm 2012; gi&aacute;o dục ph&aacute;p luật, gi&aacute;o dục giới t&iacute;nh, ph&ograve;ng, chống ma t&uacute;y, tệ nạn mại d&acirc;m, ph&ograve;ng, chống HIV/AIDS, ph&ograve;ng, chống tội phạm v&agrave; c&aacute;c tệ nạn x&atilde; hội; ph&ograve;ng, chống t&aacute;c hại của tr&ograve; chơi điện tử với nội dung xấu; gi&aacute;o dục an to&agrave;n giao th&ocirc;ng; c&ocirc;ng t&aacute;c đảm bảo an ninh, trật tự trong trường học.<br />\\r\\n- Tổ chức giao lưu TDTT giữa sinh vi&ecirc;n kh&oacute;a cũ v&agrave; kh&oacute;a mới. Nội dung Đề &aacute;n <em>&rdquo;Tuy&ecirc;n truyền, phổ biến, gi&aacute;o dục ph&aacute;p luật về trật tự, an to&agrave;n giao th&ocirc;ng cho c&aacute;n bộ, giảng vi&ecirc;n, nh&acirc;n vi&ecirc;n thanh ni&ecirc;n, sinh vi&ecirc;n trường Đại học C&ocirc;ng nghệ GTVT&rdquo;</em>.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>3. Chương tr&igrave;nh c&ocirc;ng t&aacute;c Đo&agrave;n, Hội v&agrave; phong tr&agrave;o thanh ni&ecirc;n năm học 2015 - 2016</strong></em></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>Giới thiệu về Đo&agrave;n Thanh ni&ecirc;n Cộng sản Hồ Ch&iacute; Minh, Hội sinh vi&ecirc;n Nh&agrave; trường, Chương tr&igrave;nh c&ocirc;ng t&aacute;c Đo&agrave;n, Hội v&agrave; phong tr&agrave;o Thanh ni&ecirc;n Nh&agrave; trường năm học 2015 &ndash; 2016; tổ chức giao lưu văn nghệ trong tuần sinh hoạt c&ocirc;ng d&acirc;n đầu kh&oacute;a.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><strong>III. THỜI GIAN THỰC HI&Ecirc;N</strong></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>Đợt 1 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; :</strong></em> Từ 07/09/2015 đến 19/09/2015</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>Đợt 2 (dự kiến) :</strong></em> Từ 21/09/2015 đến 27/09/2015</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><em><strong>Đợt 3 (dự kiến) :</strong></em> Từ 12/10/2015 đến 18/10/2015</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><strong>IV. TỔ CHỨC THỰC HIỆN</strong></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>1. Ph&ograve;ng C&ocirc;ng t&aacute;c HSSV chủ tr&igrave; việc tổ chức <em>&ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n &ndash; HSSV&rdquo;</em> đầu năm học 2015 - 2016, li&ecirc;n hệ v&agrave; mời c&aacute;c b&aacute;o c&aacute;o vi&ecirc;n, sắp xếp thời gian biểu.<br />\\r\\n2. Ph&ograve;ng H&agrave;nh ch&iacute;nh - Quản trị ở c&aacute;c cơ sở chuẩn bị hội trường, market, trang &acirc;m loa đ&agrave;i, b&agrave;n ghế&hellip;v&agrave; bố tr&iacute; ăn, ở, đi lại cho c&aacute;c b&aacute;o c&aacute;o vi&ecirc;n.<br />\\r\\n3. C&aacute;c khoa chuy&ecirc;n ng&agrave;nh cử giảng vi&ecirc;n b&aacute;o c&aacute;o về nội dung chương tr&igrave;nh đ&agrave;o tạo, điều kiện đội ngũ, cơ sở vật chất phục vụ học tập của Khoa; nhu cầu nguồn nh&acirc;n lực từng ng&agrave;nh cho sinh vi&ecirc;n thuộc Khoa quản l&yacute;; c&aacute;n sự quản sinh, phối hợp với đội Sinh vi&ecirc;n t&igrave;nh nguyện, theo d&otilde;i t&igrave;nh h&igrave;nh học tập, ổn định trật tự, nắm bắt sĩ số sinh vi&ecirc;n tham gia sinh hoạt thuộc đơn vị m&igrave;nh phụ tr&aacute;ch, cung cấp số liệu về t&igrave;nh h&igrave;nh học tập, sinh hoạt của sinh vi&ecirc;n cho ph&ograve;ng C&ocirc;ng t&aacute;c HSSV.<br />\\r\\n4. Đo&agrave;n TNCSHCM, Hội Sinh vi&ecirc;n Nh&agrave; trường phối hợp với Ph&ograve;ng C&ocirc;ng t&aacute;c HSSV, tổ bộ m&ocirc;n TDTT tổ chức c&aacute;c hoạt động giao lưu văn nghệ, thể thao tạo kh&ocirc;ng kh&iacute; vui tươi l&agrave;nh mạnh, g&oacute;p phần n&acirc;ng cao chất lượng đợt sinh hoạt.<br />\\r\\n5. C&aacute;c b&aacute;o c&aacute;o vi&ecirc;n c&oacute; tr&aacute;ch nhiệm nghi&ecirc;n cứu kỹ c&aacute;c t&agrave;i liệu tham khảo, chuẩn bị đầy đủ những th&ocirc;ng tin cần thiết để thực hiện c&oacute; hiệu quả những nội dung được ph&acirc;n c&ocirc;ng; chuẩn bị c&aacute;c c&acirc;u hỏi cho b&agrave;i viết thu hoạch của sinh vi&ecirc;n, tham gia chấm c&aacute;c b&agrave;i vi&ecirc;t thu hoạch.<br />\\r\\n6. Chế độ cho b&aacute;o c&aacute;o vi&ecirc;n v&agrave; c&aacute;n bộ quản l&yacute; (bao gồm thời gian chuẩn bị đề cương, b&agrave;i gi&agrave;ng, giờ giảng, chấm b&agrave;i viết thu hoạch của sinh vi&ecirc;n, tham gia quản l&yacute; lớp học) căn cứ định mức giảng dạy của giảng vi&ecirc;n v&agrave; định mức chi ti&ecirc;u nội bộ hiện h&agrave;nh. Đối với b&aacute;o c&aacute;o vi&ecirc;n của c&aacute;c Bộ, ng&agrave;nh Trung ương, chế độ bồi dưỡng b&aacute;o c&aacute;o do Hiệu trưởng quyết định.<br />\\r\\n7. Cấp giấy chứng nhận ho&agrave;n th&agrave;nh Chương tr&igrave;nh cho Sinh vi&ecirc;n</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>- Sau đợt học tập, mỗi sinh vi&ecirc;n đều phải dự kiểm tra đ&aacute;nh gi&aacute; kết quả học tập. Nh&agrave; trường cấp giấy chứng nhận cho những sinh vi&ecirc;n đ&atilde; ho&agrave;n th&agrave;nh, đạt y&ecirc;u cầu chương tr&igrave;nh học tập <em>&ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n - HSSV&rdquo;</em> năm học 2015 - 2016.<br />\\r\\n- HSSV chưa ho&agrave;n th&agrave;nh Chương tr&igrave;nh học tập phải học b&ugrave; v&agrave; dự kiểm tra đ&aacute;nh gi&aacute; &nbsp;trong thời gian của năm học do Nh&agrave; trường sắp xếp.<br />\\r\\n- Kết quả đợt học tập l&agrave; một trong những ti&ecirc;u ch&iacute; để đ&aacute;nh gi&aacute; điểm r&egrave;n luyện v&agrave; x&eacute;t danh hiệu thi đua v&agrave; c&aacute;c phần thưởng kh&aacute;c cho sinh vi&ecirc;n năm học 2015 -2016.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>Tr&ecirc;n đ&acirc;y l&agrave; kế hoạch tổ chức<em> &ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n &ndash; HSSV&rdquo;</em> đầu năm học 2015 - 2016. Nội dung chi tiết, thời gian, địa điểm, ph&acirc;n c&ocirc;ng b&aacute;o c&aacute;o vi&ecirc;n, theo d&otilde;i thời gian biểu k&egrave;m theo bản kể hoạch n&agrave;y, y&ecirc;u cầu c&aacute;c đơn vị triển khai thực hiện nghi&ecirc;m t&uacute;c, đạt hiệu quả.</p>', 1, 'vn', 1, 8, 'news', 2, 1441077946, 1441415536, NULL, 1),
(26, 'Thông báo Kế hoạch tập luyện, tổng duyệt và tham gia tổ chức Lễ kỷ niệm 70 năm Ngày truyền thống và Đại hội thi đua yêu nước Ngành Giao thông Vận tải', '47bf2acacddbe4602a47d11d9532c0ec_w250_h170_cp2.jpg', 'Ban Chấp hành Đoàn Thanh niên Trường ĐH Công nghệ GTVT phối hợp cùng Ban Chấp hành Đoàn Thanh niên Bộ GTVT xây dựng kế hoạch tập luyện, tổng duyệt và tổ chức Chương trình', '<p>Ban Chấp h&agrave;nh Đo&agrave;n Thanh ni&ecirc;n&nbsp;<a href=\\"http://utt.edu.vn/\\">Trường ĐH C&ocirc;ng nghệ GTVT</a> phối hợp c&ugrave;ng Ban Chấp h&agrave;nh Đo&agrave;n Thanh ni&ecirc;n&nbsp;Bộ GTVT x&acirc;y dựng kế hoạch tập luyện, tổng duyệt v&agrave; tổ chức Chương tr&igrave;nh, cụ thể như sau:</p>\\r\\n\\r\\n<p><strong>I. NỘI DUNG C&Ocirc;NG VIỆC CH&Iacute;NH</strong></p>\\r\\n\\r\\n<p><em><strong>1. Ng&agrave;y 13/8/2015 (thứ Năm):</strong></em> bắt đầu l&uacute;c 17h30&rsquo; tại Hội trường ĐHCNGTVT</p>\\r\\n\\r\\n<table border=\\"1\\">\\r\\n	<tbody>\\r\\n		<tr>\\r\\n			<td colspan=\\"4\\" style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>Th&agrave;nh phần: NH&Oacute;M 1</strong></p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>STT</strong></p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>Lớp</strong></p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>C&aacute;n bộ phụ tr&aacute;ch lớp</strong></p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p><strong>C&aacute;n bộ P. CTHSSV</strong></p>\\r\\n			</td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>1</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>63DCDD02</p>\\r\\n			</td>\\r\\n			<td style=\\"text-align:center; vertical-align:middle\\">\\r\\n			<p>Gi&aacute;p Văn Lợi</p