(60, 'Ban Giám hiệu trường Đại học Công nghệ GTVT', 'logo-utt-border3.png', 'Ban Giám hiệu trường Đại học Công nghệ GTVT', '<table border=\\"0\\" style=\\"height:201px\\">\\r\\n	<tbody>\\r\\n		<tr>\\r\\n			<td><img alt=\\"Hiệu trưởng: PGS.TS.Đào Văn Đông\\" src=\\"http://utt.edu.vn/home/images/stories/dr-dao-van-dong.jpg\\" style=\\"display:block; height:367px; margin-left:auto; margin-right:auto; width:550px\\" /></td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td><strong>Hiệu trưởng: PGS.TS Đ&agrave;o Văn Đ&ocirc;ng</strong></td>\\r\\n		</tr>\\r\\n	</tbody>\\r\\n</table>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<hr />\\r\\n<table border=\\"0\\">\\r\\n	<tbody>\\r\\n		<tr>\\r\\n			<td><img alt=\\"Phó hiệu trưởng: TS.Vũ Ngọc Khiêm\\" src=\\"http://utt.edu.vn/home/images/stories/bgh04.jpg\\" style=\\"height:353px; width:500px\\" /></td>\\r\\n			<td><img alt=\\"Phó hiệu trưởng: TS. Nguyễn Hoàng Long\\" src=\\"http://utt.edu.vn/home/images/stories/dr-nguyen-hoang-long.jpg\\" style=\\"height:367px; width:550px\\" /></td>\\r\\n		</tr>\\r\\n		<tr>\\r\\n			<td><strong>Ph&oacute; hiệu trưởng: TS. Vũ Ngọc Khi&ecirc;m</strong></td>\\r\\n			<td><strong>Ph&oacute; hiệu trưởng:&nbsp;TS. Nguyễn Ho&agrave;ng Long</strong></td>\\r\\n		</tr>\\r\\n	</tbody>\\r\\n</table>', 1, 'vn', 1, 8, 'news', 5, 1441098587, 1441213535, NULL, 1),
(61, 'BCH Công đoàn trường Đại học Công nghệ GTVT khóa V', 'logo-utt-border2.png', 'BCH Công đoàn trường Đại học Công nghệ GTVT khóa V', '<p>BCH C&ocirc;ng đo&agrave;n trường Đại học C&ocirc;ng nghệ &nbsp;GTVT kh&oacute;a V</p>\\r\\n\\r\\n<div><strong>Địa chỉ:</strong> Ph&ograve;ng 306, Khu hiệu bộ- Trường Đại học C&ocirc;ng nghệ GTVT - Số 54 Triều Kh&uacute;c- Thanh Xu&acirc;n- H&agrave; Nội</div>\\r\\n\\r\\n<div><strong>Điện thoại:</strong> (04) 38543091&nbsp; -&nbsp; <strong>Email:</strong> <a href=\\"mailto:congdoan@utt.edu.vn\\">congdoan@utt.edu.vn</a></div>\\r\\n\\r\\n<div>&nbsp;</div>\\r\\n\\r\\n<h3><strong>* Tổ chức nh&acirc;n sự: </strong></h3>\\r\\n\\r\\n<p><img alt=\\"\\" src=\\"http://utt.edu.vn/home/images/stories/tranvanbinh.jpg\\" style=\\"height:340px; width:250px\\" /></p>\\r\\n\\r\\n<p>- <strong>Chủ tịch:</strong> ThS. Trần Văn B&igrave;nh</p>\\r\\n\\r\\n<p>- <strong>Ph&oacute; chủ tịch:</strong> ThS. NCS C&ocirc;ng Minh Quang</p>', 1, 'vn', 1, 8, 'news', 45, 1441098652, 1441214371, NULL, 1),
(62, 'BCH Đoàn trường Đại học Công nghệ GTVT khóa VI', 'logo-utt-border1.png', 'BCH Đoàn trường Đại học Công nghệ GTVT khóa VI', '<div><strong>Địa chỉ:</strong> Ph&ograve;ng 312, Khu hiệu bộ- Trường Đại học C&ocirc;ng nghệ GTVT 54 Triều Kh&uacute;c- Thanh Xu&acirc;n- H&agrave; Nội</div>\\r\\n\\r\\n<p>&nbsp;\\r\\n<div><strong>Điện thoại:</strong> (04) 35520949 - <strong>Email:</strong> <a href=\\"\\\\\\">thanhnien@utt.edu.vn</a></div>\\r\\n</p>\\r\\n\\r\\n<p>&nbsp;\\r\\n<p><strong>* Tổ chức nh&acirc;n sự:</strong><br />\\r\\n- B&iacute; thư: Ths.NCS. L&ecirc; Ho&agrave;ng Anh<br />\\r\\n- Ph&oacute; B&iacute; thư: Ths.NCS. B&ugrave;i Văn Trầm<br />\\r\\n- Ph&oacute; B&iacute; thư : Ths.NCS. Nguyễn Văn Chưởng<br />\\r\\n- UVTV- B&iacute; thư LCĐ Cơ sở đ&agrave;o tạo Th&aacute;i Nguy&ecirc;n: Ths. Trịnh Minh Ho&agrave;ng<br />\\r\\n- UVTV- Chủ tịch Hội sinh vi&ecirc;n trường: B&ugrave;i Đức Hưng - 63DCKT05<br />\\r\\n<strong>* Đo&agrave;n cơ sở:</strong><br />\\r\\n- Li&ecirc;n chi đo&agrave;n cơ sở đ&agrave;o tạo Vĩnh Y&ecirc;n:<br />\\r\\nĐịa chỉ: 278 đường Lam Sơn, P. Đồng T&acirc;m, Tp. Vĩnh Y&ecirc;n, Tỉnh Vĩnh Ph&uacute;c. ĐT: (0211) 3867410; Email:</p>\\r\\n</p>\\r\\n\\r\\n<p>- Li&ecirc;n chi đo&agrave;n cơ sở đ&agrave;o tạo Th&aacute;i Nguy&ecirc;n:</p>\\r\\n\\r\\n<p>Địa chỉ: KM6- QL3, P.T&acirc;n Thịnh, TP. Th&aacute;i Nguy&ecirc;n, Tỉnh Th&aacute;i Nguy&ecirc;n. ĐT : ( 0280) 3848544; Email:</p>\\r\\n\\r\\n<p><strong>* Hội sinh vi&ecirc;n trường:</strong><br />\\r\\nĐịa chỉ: Nh&agrave; A4, Trường Đại học C&ocirc;ng nghệ GTVT- 54 Triều Kh&uacute;c- Thanh Xu&acirc;n- H&agrave; Nội; Email: <a href=\\"\\\\\\">sinhvien@utt.edu.vn</a></p>', 1, 'vn', 1, 8, 'news', 46, 1441098776, 1441214290, NULL, 1),
(63, 'Giới thiệu Logo Trường Đại học Công nghệ Giao thông Vận tải', 'logo-utt-border.png', 'Giới thiệu Logo Trường Đại học Công nghệ Giao thông Vận tải', '<div><strong>&Yacute; nghĩa logo trường Đại học C&ocirc;ng nghệ GTVT</strong></div>\\r\\n\\r\\n<p><img alt=\\"alt\\" src=\\"http://utt.edu.vn/home/images/stories/logo-utt-border.png\\" style=\\"width:500px\\" /></p>\\r\\n\\r\\n<h1><strong>Logo được chia th&agrave;nh hai phần: phần biểu tượng v&agrave; phần chữ:</strong></h1>\\r\\n\\r\\n<p>- Phần biểu tượng được c&aacute;ch điệu từ 3 chữ c&aacute;i tiếng Anh UTT (<strong>U</strong>niversity of <strong>T</strong>ransport <strong>T</strong>echnology) v&agrave; lấy &yacute; tưởng từ c&acirc;y cầu treo d&acirc;y v&otilde;ng, l&agrave; một c&ocirc;ng tr&igrave;nh đẹp trong ng&agrave;nh GTVT. H&igrave;nh ảnh trụ cầu v&agrave; nhịp cầu được c&aacute;ch điệu từ hai chữ c&aacute;i T,T. Chữ U được c&aacute;ch điệu như sợi c&aacute;p treo của c&acirc;y cầu, gắn 2 b&ecirc;n trụ vững chắc. Phần &acirc;m bản nằm giữa kết cấu nhịp v&agrave; d&acirc;y c&aacute;p treo l&agrave; h&igrave;nh tượng quyển s&aacute;ch đang mở rộng thể hiện truyền thống trong sự nghiệp gi&aacute;o dục đ&agrave;o tạo của nh&agrave; trường. Tổng thể logo mang h&igrave;nh ảnh một chiếc cổng lớn như dang tay đ&oacute;n ch&agrave;o mọi người đến học tập v&agrave; cộng t&aacute;c.</p>\\r\\n\\r\\n<p>- Phần chữ l&agrave; t&ecirc;n tiếng Anh của trường Đại học c&ocirc;ng nghệ GTVT: University of Transport Technology tạo th&agrave;nh phần nền m&oacute;ng vững chắc cho c&acirc;y cầu, biểu tượng của sự ổn định v&agrave; ph&aacute;t triển bền vững của nh&agrave; trường.&nbsp;</p>\\r\\n\\r\\n<p>M&agrave;u chủ đạo của logo l&agrave; m&agrave;u cam, sắc m&agrave;u của sự thịnh vượng, th&agrave;nh c&ocirc;ng v&agrave; những gi&aacute; trị bền l&acirc;u. Thiết kế độc đ&aacute;o, ấn tượng, mang t&iacute;nh biểu cảm cao, dễ nhớ, dễ nhận biết, dễ thể hiện tr&ecirc;n mọi chất liệu, đặc biệt c&oacute; thể thiết kế l&agrave;m cổng ch&iacute;nh, một c&ocirc;ng tr&igrave;nh c&oacute; t&iacute;nh biểu tượng của nh&agrave; trường.</p>', 1, 'vn', 1, 8, 'news', 47, 1441098835, 1441214341, NULL, 1),
(74, 'Ban Giám hiệu trường Đại học Công nghệ GTVT', 'defaut_thumbnail_utt1.jpg', 'Ban Giám hiệu trường Đại học Công nghệ GTVT', '<div>&nbsp;</div>\r\n\r\n<table border="0" style="height:201px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="Hiệu trưởng: PGS.TS.Đào Văn Đông" src="http://utt.edu.vn/home/images/stories/dr-dao-van-dong.jpg" style="display:block; height:367px; margin-left:auto; margin-right:auto; width:550px" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hiệu trưởng: PGS.TS Đ&agrave;o Văn Đ&ocirc;ng</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<table border="0">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt="Phó hiệu trưởng: TS.Vũ Ngọc Khiêm" src="http://utt.edu.vn/home/images/stories/bgh04.jpg" style="height:353px; width:500px" /></p>\r\n			</td>\r\n			<td><img alt="Phó hiệu trưởng: TS. Nguyễn Hoàng Long" src="http://utt.edu.vn/home/images/stories/dr-nguyen-hoang-long.jpg" style="height:367px; width:550px" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Ph&oacute; hiệu trưởng: TS. Vũ Ngọc Khi&ecirc;m</strong></td>\r\n			<td><strong>Ph&oacute; hiệu trưởng:&nbsp;TS. Nguyễn Ho&agrave;ng Long</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 1, 'vn', 1, 8, 'news', 44, 1441213794, NULL, NULL, 1),
(64, 'Thông báo các cơ quan, đơn vị xem phim tài liệu về Ngành GTVT', '35624f0422ac91dae9a19405f96ff986_w250_h170_cp.jpg', 'Nằm trong chuỗi hoạt động kỷ niệm 70 năm ngày truyền thống ngành Giao thông vận tải (28/8/1945 - 28/8/2015) và Đại hội thi đua yêu nước ngành Giao thông vận tải (2015 - 2020), Bộ Giao thông vận tải và Đài truyền hình Việt Nam đã phối hợp thực hiện Bộ phim tài liệu "70 năm Giao thông vận tải - đi trước mở đường".', '<div>\r\n<ul>\r\n	<li><a href="http://utt.edu.vn/home/tin-tuc/thong-bao/1510-thong-bao-cac-co-quan-don-vi-xem-phim-tai-lieu-ve-nganh-gtvt?tmpl=component&amp;print=1&amp;layout=default&amp;page=" rel="nofollow" title="In bài này">&nbsp;In b&agrave;i n&agrave;y&nbsp;</a></li>\r\n	<li><a href="http://utt.edu.vn/home/component/mailto/?tmpl=component&amp;template=ja_university_t3&amp;link=9cf3ea30a2eec1385a90f7b9dc9f462cef1b8faf" title="Gửi Email bài này">Gửi Email b&agrave;i n&agrave;y</a></li>\r\n</ul>\r\n</div>\r\n\r\n<p><img alt="70 năm Giao thông vận tải - đi trước mở đường" src="http://utt.edu.vn/home/images/stories/thong-bao-cac-co-quan-don-vi-xem-phim-tai-lieu-ve-nganh-gtvt.jpg" style="height:898px; width:681px" /></p>', 1, 'vn', 1, 8, 'news', 49, 1441211827, NULL, NULL, 1),
(65, 'Thông báo phân công Giáo viên chủ nhiệm - Cố vấn học tập cho các lớp khoá 66', '8ffacbe7007af74bf22da65d812ab008_w250_h170_cp.jpg', 'Thực hiện Quy định Giáo viên chủ nhiệm - Cố vấn học tập của Trường Đại học Công nghệ GTVT ban hành kèm theo Quyết định số 1816/QĐ-ĐHCNGTVT-CTHSSV ngày 12/8/2013 của Hiệu trưởng Trường Đại học Công nghệ GTVT, đề nghị các Khoa phân công Giáo viên chủ nhiệm - Cố vấn học tập cho các lớp khóa 66 (có danh sách lớp kèm theo), ưu tiên giảng viên chuyên ngành trước (kể cả giảng viên kiêm nhiệm) sau đó đến giảng viên ở các khoa Cơ sở kỹ thuật, khoa Khoa học cơ bản, khoa Lý luận chính tr', '<p>Thực hiện Quy định Gi&aacute;o vi&ecirc;n chủ nhiệm - Cố vấn học tập của Trường Đại học C&ocirc;ng nghệ GTVT ban h&agrave;nh k&egrave;m theo Quyết định số 1816/QĐ-ĐHCNGTVT-CTHSSV ng&agrave;y 12/8/2013 của Hiệu trưởng Trường Đại học C&ocirc;ng nghệ GTVT, đề nghị c&aacute;c Khoa ph&acirc;n c&ocirc;ng Gi&aacute;o vi&ecirc;n chủ nhiệm - Cố vấn học tập cho c&aacute;c lớp kh&oacute;a 66 (c&oacute; danh s&aacute;ch lớp k&egrave;m theo), ưu ti&ecirc;n giảng vi&ecirc;n chuy&ecirc;n ng&agrave;nh trước (kể cả giảng vi&ecirc;n ki&ecirc;m nhiệm) sau đ&oacute; đến giảng vi&ecirc;n ở c&aacute;c khoa Cơ sở kỹ thuật, khoa Khoa học cơ bản, khoa L&yacute; luận ch&iacute;nh trị.</p>\r\n\r\n<p>C&aacute;c Giảng vi&ecirc;n đ&atilde; tham gia c&ocirc;ng t&aacute;c GVCN - CVHT đối với c&aacute;c kh&oacute;a kh&aacute;c đang theo học tại Trường th&igrave; kh&ocirc;ng tham gia c&ocirc;ng t&aacute;c n&agrave;y đối với kh&oacute;a 66, đảm bảo mỗi giảng vi&ecirc;n chỉ chủ nhiệm một lớp sinh vi&ecirc;n. Danh s&aacute;ch gửi về c&aacute;c Khoa chuy&ecirc;n ng&agrave;nh, c&aacute;c Khoa chuy&ecirc;n ng&agrave;nh tập hợp, ph&acirc;n c&ocirc;ng v&agrave; gửi về ph&ograve;ng C&ocirc;ng t&aacute;c HSSV (P.102H3) chậm nhất đến hết ng&agrave;y 31/8/2015.</p>', 1, 'vn', 1, 8, 'news', 2, 1441211874, NULL, NULL, 1),
(66, 'Thông báo Phân công nhiệm vụ xây dựng và báo cáo các nội dung giảng dạy trong tuần sinh hoạt công dân đầu khoá đối với các lớp sinh viên khoá 66', '47bf2acacddbe4602a47d11d9532c0ec_w250_h170_cp.jpg', 'Thực hiện Kế hoạch số 1989/KH-ĐHCNGTVT ngày 19 tháng 8 năm 2015 của Hiệu trưởng trường Đại học Công nghệ GTVT về việc tổ chức “Tuần sinh hoạt công dân - HSSV” năm học 2015 - 2016. Ban Giám Hiệu phân công nhiệm vụ cho các đơn vị xây dựng và báo cáo các nội dung giảng dạy trong tuần sinh hoạt công dân đầu khóa đối với các lớp sinh viên khóa 66, như sau:', '<p>Thực hiện Kế hoạch số 1989/KH-ĐHCNGTVT ng&agrave;y 19 th&aacute;ng 8 năm 2015 của Hiệu trưởng trường Đại học C&ocirc;ng nghệ GTVT về việc tổ chức <em>&ldquo;Tuần sinh hoạt c&ocirc;ng d&acirc;n - HSSV&rdquo;</em> năm học 2015 - 2016. Ban Gi&aacute;m Hiệu ph&acirc;n c&ocirc;ng nhiệm vụ cho c&aacute;c đơn vị x&acirc;y dựng v&agrave; b&aacute;o c&aacute;o c&aacute;c nội dung giảng dạy trong tuần sinh hoạt c&ocirc;ng d&acirc;n đầu kh&oacute;a đối với c&aacute;c lớp sinh vi&ecirc;n kh&oacute;a 66, như sau:</p>\r\n\r\n<div>\r\n<table border="1">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>STT</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>Nội dung giảng dạy</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>Đơn vị phụ tr&aacute;ch</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>Thời gian</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>1</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Qu&aacute;n triệt phương hướng nhiệm vụ của Đảng bộ Trường nhiệm kỳ 2015 - 2020; th&ocirc;ng tin về t&igrave;nh h&igrave;nh kinh tế, ch&iacute;nh trị, x&atilde; hội trong nước, khu vực v&agrave; quốc tế; nội dung c&ocirc;ng t&aacute;c trọng t&acirc;m của Trường năm học 2015 - 2016; chiến lược ph&aacute;t triển Trường đến năm 2020 v&agrave; tầm nh&igrave;n đến năm 2030; gi&aacute;o dục truyền thống nh&agrave; trường hướng tới kỷ niệm 70 năm th&agrave;nh lập Trường.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ban Gi&aacute;m hiệu</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>01 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>2</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Giới thiệu chung về cấu tr&uacute;c CTĐT; chuẩn đầu ra; kế hoạch đ&agrave;o tạo năm học 2015-2016; li&ecirc;n th&ocirc;ng giữa c&aacute;c ng&agrave;nh đ&agrave;o tạo; Quy chế đ&agrave;o tạo theo t&iacute;n chỉ v&agrave; c&aacute;c quy định về đ&agrave;o tạo của Trường; giới thiệu v&agrave; hướng dẫn sử dụng chương tr&igrave;nh quản l&yacute; đ&agrave;o tạo đối với sinh vi&ecirc;n đăng k&yacute; học phần, tra cứu v&agrave; khai th&aacute;c th&ocirc;ng tin tr&ecirc;n mạng.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ph&ograve;ng Đ&agrave;o tạo</p>\r\n\r\n			<p>Ph&ograve;ng ĐBCLĐT</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>02 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>3</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Quy chế HSSV; Quy chế điểm r&egrave;n luyện; Quy định c&aacute;n bộ lớp; hướng dẫn khai b&aacute;o hồ sơ tr&ecirc;n chương tr&igrave;nh quản l&yacute; đ&agrave;o tạo; quy định thi sinh vi&ecirc;n giỏi; sinh vi&ecirc;n NCKH v&agrave; c&aacute;c quy định li&ecirc;n quan đến sinh vi&ecirc;n; học bổng KKHT, học ph&iacute;; Chế độ ch&iacute;nh s&aacute;ch, BHYT, BHTT, vay vốn t&iacute;n dụng; Quy tr&igrave;nh h&agrave;nh ch&iacute;nh một cửa; c&ocirc;ng t&aacute;c GVCN - CVHT.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ph&ograve;ng CTHSSV</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>02 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>4</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Giới thiệu nhu cầu nguồn nh&acirc;n lực ng&agrave;nh đ&agrave;o tạo; y&ecirc;u cầu chuẩn đầu ra đối với chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo v&agrave; chi tiết nội dung chương tr&igrave;nh đ&agrave;o tạo; điều kiện đội ngũ giảng vi&ecirc;n, cơ sở vật chất, trang thiết bị phục vụ đ&agrave;o tạo, NCKH; phương ph&aacute;p học tập, kế hoạch học tập cụ thể học kỳ 1 cho sinh vi&ecirc;n thuộc Khoa chuy&ecirc;n ng&agrave;nh quản l&yacute;.&nbsp;</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>C&aacute;c Khoa</p>\r\n\r\n			<p>chuy&ecirc;n m&ocirc;n</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>02 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>5</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Giới thiệu c&aacute;c chương tr&igrave;nh đ&agrave;o tạo bằng tiếng nước ngo&agrave;i; chương tr&igrave;nh dự bị đại học v&agrave; thạc sĩ tại Ph&aacute;p</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ph&ograve;ng KHCN -HTQT</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>1/3 buổi</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>6</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Giới thiệu Trung t&acirc;m th&ocirc;ng tin thư viện, nguồn t&agrave;i liệu phục vụ học tập, nghi&ecirc;n cứu cho sinh vi&ecirc;n.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Thư viện</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>1/3 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>7</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Phương thức nộp học ph&iacute;, c&aacute;c khoản thu kh&aacute;c (nếu c&oacute;)</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ph&ograve;ng TCKT</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>1/3 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>8</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Chương tr&igrave;nh c&ocirc;ng t&aacute;c của Đo&agrave;n Thanh ni&ecirc;n CSHCM nh&agrave; trường năm học 2015 - 2016; c&aacute;c hoạt động ngoại kh&oacute;a; c&aacute;c c&acirc;u lạc bộ sinh vi&ecirc;n.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>ĐTN</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>01 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>9</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Giao lưu thể thao, VHVN.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Tổ TDTT</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>03 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>10</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>GVCN - CVHT sinh hoạt với lớp sinh vi&ecirc;n.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>GVCN</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>01 buổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>11</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Đ&aacute;nh gi&aacute; kết quả đợt học tập tuần sinh hoạt c&ocirc;ng d&acirc;n của sinh vi&ecirc;n.</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>Ph&ograve;ng CTHHSSV v&agrave; c&aacute;c đơn vị</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>02 buổi</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Căn cứ c&aacute;c nội dung được ph&acirc;n c&ocirc;ng, Nh&agrave; trường y&ecirc;u cầu l&atilde;nh đạo c&aacute;c đơn vị chỉ đạo x&acirc;y dựng v&agrave; cử giảng vi&ecirc;n b&aacute;o c&aacute;o c&aacute;c nội dung trong tuần sinh hoạt c&ocirc;ng d&acirc;n đầu kh&oacute;a cho sinh vi&ecirc;n kh&oacute;a 66. Nội dung b&aacute;o c&aacute;o được tr&igrave;nh b&agrave;y bằng Powerpoint v&agrave; gửi về Ban Gi&aacute;m Hiệu duyệt th&ocirc;ng qua ph&ograve;ng C&ocirc;ng t&aacute;c HSSV trước ng&agrave;y 03/9/2015. Ph&ograve;ng C&ocirc;ng t&aacute;c HSSV lập thời kh&oacute;a biểu gửi c&aacute;c đơn vị trước ng&agrave;y 03/9/2015.</p>\r\n</div>', 1, 'vn', 1, 8, 'news', 2, 1441211992, NULL, NULL, 1),
(67, 'Khai giảng Lớp Bồi dưỡng lý luận chính trị dành cho Đảng viên mới và Lớp Bồi dưỡng kết nạp Đảng cho các quần chúng ưu tú năm 2015', 'd08a5890504774e812b6b9192ceb8b17_w250_h170_cp.jpg', 'Sáng ngày 29/8, Trường Đại học Công nghệ GTVT tổ chức Khai giảng Lớp Bồi dưỡng lý luận chính trị dành cho Đảng viên mới và Lớp Bồi dưỡng kết nạp Đảng cho các quần chúng ưu tú của các Chi bộ, tổ chức đoàn thể trong toàn trường.', '<p>S&aacute;ng ng&agrave;y 29/8, Trường Đại học C&ocirc;ng nghệ GTVT tổ chức Khai giảng Lớp Bồi dưỡng l&yacute; luận ch&iacute;nh trị d&agrave;nh cho Đảng vi&ecirc;n mới v&agrave; Lớp Bồi dưỡng kết nạp Đảng cho c&aacute;c quần ch&uacute;ng ưu t&uacute; của c&aacute;c Chi bộ, tổ chức đo&agrave;n thể trong to&agrave;n trường.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-01.jpg" style="height:450px; width:675px" /><br />\r\n<em>To&agrave;n cảnh lớp Bồi dưỡng l&yacute; luận ch&iacute;nh trị d&agrave;nh cho Đảng vi&ecirc;n mới v&agrave; lớp Bồi dưỡng kết nạp Đảng cho c&aacute;c quần ch&uacute;ng ưu t&uacute;</em></p>\r\n\r\n<p>Đến dự lễ khai giảng c&oacute; TS. Nguyễn Ho&agrave;ng Long- Ph&oacute; B&iacute; thư Đảng bộ, Ph&oacute; Hiệu trưởng Nh&agrave; trường; đồng ch&iacute; Trần Quang Dũng - Nguy&ecirc;n Ph&oacute; B&iacute; thư Đảng bộ, Nguy&ecirc;n Ph&oacute; Hiệu trưởng Nh&agrave; trường; đồng ch&iacute; Trần Văn B&igrave;nh - Chủ tịch C&ocirc;ng đo&agrave;n Nh&agrave; trường; l&atilde;nh đạo c&aacute;c Ph&ograve;ng, Ban, Khoa đơn vị chức năng trong Nh&agrave; trường c&ugrave;ng c&aacute;c đồng ch&iacute; trong Ban chỉ đạo, tổ chức lớp học v&agrave; 46 đảng vi&ecirc;n dự bị, 243 học vi&ecirc;n l&agrave; quần ch&uacute;ng ưu t&uacute; được c&aacute;c chi bộ, c&aacute;c tổ chức đo&agrave;n thể lựa chọn v&agrave; giới thiệu tham gia lớp học.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-02.jpg" style="height:434px; width:651px" /><br />\r\n<em>TS. Nguyễn Ho&agrave;ng Long - Ph&oacute; B&iacute; thư Đảng bộ, Ph&oacute; Hiệu trưởng Nh&agrave; trường ph&aacute;t biểu khai mạc lớp học</em></p>\r\n\r\n<p>Sau phần tuy&ecirc;n bộ l&yacute; do, giới thiệu Đại biểu v&agrave; c&ocirc;ng bố c&aacute;c văn bản li&ecirc;n quan đến kế hoạch v&agrave; c&ocirc;ng t&aacute;c tổ chức lớp học, TS. Nguyễn Ho&agrave;ng Long - Ph&oacute; B&iacute; thư Đảng Bộ, Ph&oacute; Hiệu trưởng Nh&agrave; trường đ&atilde; thay mặt cho Đảng bộ Trường ph&aacute;t biểu khai mạc v&agrave; chỉ đạo lớp học. Trong nội dung ph&aacute;t biểu của TS. Nguyễn Ho&agrave;ng Long đ&atilde; đặc biệt nhấn mạnh &yacute; nghĩa v&agrave; tầm quan trọng của việc bồi dưỡng kiến thức về Đảng nhằm cung cấp h&agrave;nh trang cần thiết cho c&aacute;c đảng vi&ecirc;n mới v&agrave; c&aacute;c quần ch&uacute;ng ưu t&uacute; trước khi t&igrave;nh nguyện gia nhập tổ chức Ch&iacute;nh Đảng duy nhất đại biểu trung th&agrave;nh cho lợi &iacute;ch của giai cấp c&ocirc;ng nh&acirc;n v&agrave; người lao động ở Việt Nam; đồng thời TS. Nguyễn Ho&agrave;ng Long cũng động vi&ecirc;n v&agrave; k&ecirc;u gọi c&aacute;c đảng vi&ecirc;n mới, quần ch&uacute;ng ưu t&uacute; ph&aacute;t huy tinh thần tr&aacute;ch nhiệm, thể hiện t&igrave;nh cảm, &yacute; ch&iacute; phấn đấu vươn l&ecirc;n Đảng bằng việc l&agrave;m thiết thực l&agrave; tham dự đầy đủ, nghi&ecirc;m t&uacute;c v&agrave; đ&aacute;p ứng tốt c&aacute;c y&ecirc;u cầu do Ban tổ chức lớp học v&agrave; c&aacute;c giảng vi&ecirc;n, b&aacute;o c&aacute;o vi&ecirc;n đề ra.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-03.jpg" style="height:458px; width:687px" /><br />\r\n<em>Đồng ch&iacute; Nguyễn Thị Minh Nguyệt ph&aacute;t biểu tại lễ khai giảng lớp bồi dưỡng l&yacute; luận ch&iacute;nh trị d&agrave;nh cho đảng vi&ecirc;n mới</em></p>\r\n\r\n<p>Đồng ch&iacute; Nguyễn Thị Minh Nguyệt - Giảng vi&ecirc;n Khoa Kinh tế vận tải đ&atilde; thay mặt cho 46 đồng ch&iacute; l&agrave; đảng vi&ecirc;n mới tham lớp Bồi dưỡng l&yacute; luận ch&iacute;nh trị ph&aacute;t biểu cảm ơn Đảng bộ, Ban tổ chức đ&atilde; tạo mọi điều kiện thuận lợi nhất về thời gian, cơ sở vật chất để c&aacute;c học vi&ecirc;n được tham gia lớp học đầy đủ v&agrave; hứa với Đảng bộ Trường sẽ chấp h&agrave;nh tốt nội quy v&agrave; c&aacute;c y&ecirc;u cầu về nội dung chương tr&igrave;nh, ho&agrave;n th&agrave;nh Kho&aacute; học với chất lượng cao.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-04.jpg" style="height:476px; width:714px" /><br />\r\n<em>Quần ch&uacute;ng ưu t&uacute; Vũ Quỳnh Anh ph&aacute;t biểu đại diện cho 243 học vi&ecirc;n lớp bồi dưỡng kết nạp Đảng</em></p>\r\n\r\n<p>Đại diện cho 243 quần ch&uacute;ng ưu t&uacute; của Nh&agrave; trường, quần ch&uacute;ng ưu t&uacute;&nbsp;Vũ Quỳnh Anh - Giảng vi&ecirc;n Bộ m&ocirc;n L&yacute; luận ch&iacute;nh trị (Cơ sở đ&agrave;o tạo Th&aacute;i Nguy&ecirc;n) gửi lời cảm ơn ch&acirc;n th&agrave;nh đến Đảng bộ Nh&agrave; trường đ&atilde; quan t&acirc;m, tạo điều tổ chức lớp học v&agrave; hứa sẽ cố gắng ho&agrave;n th&agrave;nh kho&aacute; học với kết quả tốt nhất.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-05.jpg" style="height:449px; width:674px" /><br />\r\n<em>B&aacute;o c&aacute;o vi&ecirc;n Trần Văn B&igrave;nh - Chủ tịch C&ocirc;ng đo&agrave;n Trường b&aacute;o c&aacute;o tại lớp học bồi dưỡng kết nạp Đảng</em></p>\r\n\r\n<p>Ngay trong buổi học đầu ti&ecirc;n, sau lễ khai giảng c&aacute;c học vi&ecirc;n lớp bồi dưỡng kết nạp Đảng cho c&aacute;c quần ch&uacute;ng ưu t&uacute; đ&atilde; được đồng ch&iacute; Trần Văn B&igrave;nh - Chủ tịch C&ocirc;ng đo&agrave;n Nh&agrave; trường giới thiệu chuy&ecirc;n đề Học tập v&agrave; l&agrave;m theo tấm gương Đạo đức Hồ Ch&iacute; Minh. Tiếp đ&oacute; sẽ l&agrave; c&aacute;c chuy&ecirc;n đề giới thiệu về lịch sử Đảng Cộng sản Việt Nam; về qu&aacute; tr&igrave;nh x&acirc;y dựng v&agrave; ph&aacute;t triển của Đảng bộ Trường; về Cương lĩnh x&acirc;y dựng đất nước trong Thời kỳ qu&aacute; độ; Phấn đấu trở th&agrave;nh Đảng vi&ecirc;n Cộng sản v&agrave; Những nội dung cơ bản của Điều lệ Đảng.</p>\r\n\r\n<p><img alt="Lớp bồi dưỡng LLCT" src="http://utt.edu.vn/home/images/stories/kg-lop-boi-duong-llct-danh-cho-dang-vien-moi-va-lop-boi-duong-ket-nap-dang-2015-06.jpg" style="height:465px; width:698px" /><br />\r\n<em>B&aacute;o c&aacute;o vi&ecirc;n Trần Quang Dũng - Nguy&ecirc;n Ph&oacute; B&iacute; thư Đảng bộ, nguy&ecirc;n Ph&oacute; hiệu trưởng Nh&agrave; trường b&aacute;o c&aacute;o tại lớp bồi dưỡng l&yacute; luận ch&iacute;nh trị cho đảng vi&ecirc;n mới</em></p>\r\n\r\n<p>Tại lớp bồi dưỡng l&yacute; luận ch&iacute;nh trị cho Đảng vi&ecirc;n mới, c&aacute;c học vi&ecirc;n sẽ được qu&aacute;n triệt c&aacute;c chuy&ecirc;n đề ch&iacute;nh: Kh&aacute;i qu&aacute;t lịch sử Đảng Cộng sản Việt Nam; Cương lĩnh x&acirc;y dựng đất nước trong thời kỳ qu&aacute; độ l&ecirc;n Chủ nghĩa x&atilde; hội; Nội dung cơ bản Điều lệ đảng vi&ecirc;n Đảng Cộng sản Việt Nam; Học tập v&agrave; l&agrave;m theo tư tưởng, tấm gương đạo đức Hồ Ch&iacute; Minh v&agrave; chuy&ecirc;n đề Phấn đấu trở th&agrave;nh đảng vi&ecirc;n Đảng Cộng sản Việt Nam.&nbsp;Th&ocirc;ng qua lớp bồi dưỡng LLCT sẽ gi&uacute;p c&aacute;c học vi&ecirc;n nắm được c&aacute;c kiến thức cơ bản về chủ nghĩa M&aacute;c - L&ecirc; Nin, tư tưởng Hồ Ch&iacute; Minh, những đường lối của C&aacute;ch mạng Việt Nam; nội dung, quan điểm mới trong cương lĩnh x&acirc;y dựng đất nước thời kỳ qu&aacute; độ l&ecirc;n Chủ nghĩa x&atilde; hội, đường lối v&agrave; chiến lược ph&aacute;t triển kinh tế - x&atilde; hội, nhiệm vụ của người đảng vi&ecirc;n... Đ&acirc;y cũng l&agrave; dịp gi&uacute;p c&aacute;c học vi&ecirc;n phấn đấu, r&egrave;n luyện để trở th&agrave;nh đảng vi&ecirc;n, xứng đ&aacute;ng với danh hiệu người đảng vi&ecirc;n Đảng Cộng sản Việt Nam.</p>\r\n\r\n<p>Lớp bồi dưỡng sẽ được tổ chức từ ng&agrave;y 29/8 đến hết ng&agrave;y 31/8/2015, c&aacute;c học vi&ecirc;n đ&aacute;p ứng tốt c&aacute;c y&ecirc;u cầu về quy chế v&agrave; nội dung học tập sẽ được Đảng uỷ cấp Giấy Chứng nhận ho&agrave;n th&agrave;nh kho&aacute; học L&yacute; luận ch&iacute;nh trị d&agrave;nh cho đảng vi&ecirc;n mới v&agrave; Giấy chứng nhận cảm t&igrave;nh đảng cho c&aacute;c quần ch&uacute;ng ưu t&uacute;.</p>', 1, 'vn', 1, 8, 'news', 50, 1441212713, NULL, NULL, 1);
INSERT INTO `utt_post` (`id`, `title`, `image`, `description`, `detail`, `site_id`, `lang`, `status`, `user_id`, `post_type`, `cate_id`, `time_create`, `time_update`, `sort`, `is_top`) VALUES
(68, 'Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật xây dựng Cầu đường sắt', '5c2cb47a06da2c238efe28c7e8bddf4a_w250_h170_cp.jpg', 'Chương trình đào tạo Đại học Chính quy Công nghệ kỹ thuật xây dựng cầu đường sắt nhằm trang bị cho người học sau khi tốt nghiệp có kiến thức khoa học cơ bản, cơ sở, kỹ thuật chuyên môn toàn diện, năng lực thực hành nghề nghiệp cơ bản, năng lực tư duy sáng tạo, khả năng thích ứng nhanh với những biến đổi trong lĩnh vực công nghệ xây dựng cầu đường sắt; có đạo đức nghề nghiệp, có sức khỏe đáp ứng yêu cầu xây dựng và bảo vệ Tổ quốc.', '<p><strong>1. Mục ti&ecirc;u đ&agrave;o tạo chung của chuy&ecirc;n ng&agrave;nh</strong></p>\r\n\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo Đại học Ch&iacute;nh quy C&ocirc;ng nghệ kỹ thuật x&acirc;y dựng cầu đường sắt nhằm trang bị cho người học sau khi tốt nghiệp c&oacute; kiến thức khoa học cơ bản, cơ sở, kỹ thuật chuy&ecirc;n m&ocirc;n to&agrave;n diện, năng lực thực h&agrave;nh nghề nghiệp cơ bản, năng lực tư duy s&aacute;ng tạo, khả năng th&iacute;ch ứng nhanh với những biến đổi trong lĩnh vực c&ocirc;ng nghệ x&acirc;y dựng cầu đường sắt; c&oacute; đạo đức nghề nghiệp, c&oacute; sức khỏe đ&aacute;p ứng y&ecirc;u cầu x&acirc;y dựng v&agrave; bảo vệ Tổ quốc.</p>\r\n\r\n<p><strong>2. C&aacute;c c&ocirc;ng vi&ecirc;̣c sinh vi&ecirc;n phụ tr&aacute;ch sau t&ocirc;́t nghi&ecirc;̣p</strong></p>\r\n\r\n<p>C&aacute;c kỹ sư Cầu đường sắt c&oacute; thể đảm nhiệm c&aacute;c c&ocirc;ng việc nghi&ecirc;n cứu, thiết kế, tư vấn gi&aacute;m &nbsp;s&aacute;t, quản l&yacute; dự &aacute;n, thi c&ocirc;ng x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh Cầu, Đường sắt, Đường bộ;&nbsp;</p>\r\n\r\n<p><em><strong>Nơi l&agrave;m việc:</strong></em> L&agrave;m việc tại c&aacute;c cơ quan đ&agrave;o tạo - nghi&ecirc;n cứu khoa học, c&aacute;c cơ quan quản l&yacute; nh&agrave; nước, c&aacute;c đơn vị tư vấn thiết kế (kể cả c&aacute;c C&ocirc;ng ty nước ngo&agrave;i); c&aacute;c doanh nghiệp x&acirc;y dựng c&ocirc;ng tr&igrave;nh giao th&ocirc;ng, c&aacute;c Sở Giao th&ocirc;ng c&ocirc;ng ch&iacute;nh v&agrave; c&aacute;c ng&agrave;nh kinh tế kh&aacute;c.</p>\r\n\r\n<p><em><strong>C&aacute;c c&ocirc;ng việc sinh vi&ecirc;n c&oacute; thể thực hiện sau khi ra trường:</strong></em></p>\r\n\r\n<p>+ Tổ chức c&ocirc;ng t&aacute;c đo đạc khảo s&aacute;t thu thập số liệu cần thiết cho thiết kế c&ocirc;ng tr&igrave;nh cầu đường sắt;<br />\r\n+ Thiết kế; thiết kế thi c&ocirc;ng c&ocirc;ng tr&igrave;nh cầu đường sắt;<br />\r\n+ Chỉ đạo tổ chức thi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh: Đường giao th&ocirc;ng, hệ thống tho&aacute;t nước, hạ tầng kỹ thuật c&aacute;c khu đ&ocirc; thị&hellip;<br />\r\n+ Kiểm tra v&agrave; nghiệm thu chất lượng c&ocirc;ng tr&igrave;nh cầu đường sắt;<br />\r\n+ Lập, tổ chức thực hiện, quản l&yacute; c&aacute;c dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh cầu đường sắt;<br />\r\n+ Quản l&yacute; khai th&aacute;c, kiểm định chất lượng c&ocirc;ng tr&igrave;nh cầu đường sắt;<br />\r\n+ Sử dụng ngoại ngữ, c&ocirc;ng nghệ th&ocirc;ng tin.. để nghi&ecirc;n cứu, ứng dụng hiệu quả tiến khoa học - c&ocirc;ng nghệ trong lĩnh vực kỹ thuật x&acirc;y dựng &nbsp;cầu đường sắt.</p>\r\n\r\n<p><img alt="Chuyên ngành Công nghệ Kỹ thuật xây dựng Cầu đường sắt" src="http://utt.edu.vn/home/images/stories/chuong-trinh-dao-tao-chuyen-nganh-cong-nghe-ky-thuat-xay-dung-cau-duong-sat-01.jpg" style="height:661px; width:750px" /></p>\r\n\r\n<p><em><strong>C&aacute;c dự &aacute;n Đường sắt lớn đ&atilde; v&agrave; đang khởi c&ocirc;ng:</strong></em></p>\r\n\r\n<p>+ C&aacute;c g&oacute;i dự &aacute;n cải tạo n&acirc;ng cấp cầu đường sắt ( tr&ecirc;n tất cả c&aacute;c tuyến đường sắt);<br />\r\n+ Đường sắt Cao tốc Bắc &ndash; Nam;<br />\r\n+ Tuyến Đường sắt: S&agrave;i G&ograve;n &ndash; Vũng T&agrave;u; S&agrave;i G&ograve;n &ndash; Lộc Ninh; S&agrave;i G&ograve;n &ndash; Cần thơ;&hellip;<br />\r\n+ C&aacute;c tuyến Đường sắt Đ&ocirc; thị tại Tp. Hồ Ch&iacute; Minh: Bến Th&agrave;nh &ndash; Suối Ti&ecirc;n, Bến Th&agrave;nh &ndash; Tham Lương, Bến Th&agrave;nh &ndash; T&acirc;n B&igrave;nh,&hellip;<br />\r\n+ C&aacute;c tuyến Đường sắt Đ&ocirc; thị tại H&agrave; Nội: C&aacute;t Linh &ndash; H&agrave; Đ&ocirc;ng, Nhổ - Ga H&agrave; Nội, Nam Thăng Long &ndash; T&acirc;y H&agrave; Nội,&hellip;</p>\r\n\r\n<p><strong>3. Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n v&agrave; nội dung chương tr&igrave;nh học</strong></p>\r\n\r\n<p><em><strong>Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n:&nbsp;</strong></em></p>\r\n\r\n<p>+ Cung cấp cho sinh vi&ecirc;n đầy đủ c&aacute;c kiến thức khoa học cơ bản, cơ sở ng&agrave;nh để gi&uacute;p sinh vi&ecirc;n tiếp thu hiệu quả những kiến thức chuy&ecirc;n m&ocirc;n của ng&agrave;nh học;<br />\r\n+ Tăng cường c&aacute;c học phần thực h&agrave;nh thực tập, gi&uacute;p sinh vi&ecirc;n tiếp cận với m&ocirc;i trường thực tiễn, r&uacute;t ngắn khoảng c&aacute;ch giữa nh&agrave; trường v&agrave; m&ocirc;i trường l&agrave;m việc sau khi ra trường;<br />\r\n+ Chương tr&igrave;nh đ&agrave;o tạo được cập nhật những kiến thức mới, c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến của Việt Nam v&agrave; tr&ecirc;n thế giới;<br />\r\n+ Cung cấp cho sinh vi&ecirc;n những c&ocirc;ng cụ hỗ trợ học tập v&agrave; l&agrave;m việc như: c&aacute;c phần mềm chuy&ecirc;n dụng, ngoại ngữ, tin học;<br />\r\n+ R&egrave;n sinh vi&ecirc;n khả năng tư duy tự học, tự t&iacute;ch lũy bổ sung kiến thức, tư duy nghi&ecirc;n cứu khoa học để c&oacute; khả năng nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u;<br />\r\n+ R&egrave;n cho sinh vi&ecirc;n c&aacute;c kỹ năng ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute;, tổng hợp giải quyết những vấn đề trong lĩnh vực chuy&ecirc;n ng&agrave;nh; R&egrave;n luyện khả năng thuyết tr&igrave;nh, l&agrave;m việc hiệu quả theo nh&oacute;m;<br />\r\n+ Song song với học kiến thức chuy&ecirc;n ng&agrave;nh l&agrave; r&egrave;n luyện thể chất, th&aacute;i độ v&agrave; t&aacute;c phong l&agrave;m việc, th&aacute;i độ phục vụ, đạo đức nghề nghiệp&hellip; nhằm gi&uacute;p sinh vi&ecirc;n ph&aacute;t triển một c&aacute;ch to&agrave;n diện nhất.</p>\r\n\r\n<div>\r\n<table border="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>STT</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>KHỐI KIẾN THỨC</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Bắt buộc</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Tự chọn</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Tổng</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p><strong>Khối kiến thức gi&aacute;o dục đại cương</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>43</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>4</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>47</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>2</strong></p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p><strong>Kiến thức gi&aacute;o dục chuy&ecirc;n nghiệp</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>115</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>10</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>125</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2.1</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p>Kiến thức cơ sở ng&agrave;nh</p>\r\n			</td>\r\n			<td>\r\n			<p>43</p>\r\n			</td>\r\n			<td>\r\n			<p>4</p>\r\n			</td>\r\n			<td>\r\n			<p>47</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2.2</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p>Kiến thức ng&agrave;nh</p>\r\n			</td>\r\n			<td>\r\n			<p>39</p>\r\n			</td>\r\n			<td>\r\n			<p>6</p>\r\n			</td>\r\n			<td>\r\n			<p>45</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2.3</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p>Thực tập nghề nghiệp</p>\r\n			</td>\r\n			<td>\r\n			<p>21</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>21</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2.4</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p>Thực tập tốt nghiệp</p>\r\n			</td>\r\n			<td>\r\n			<p>4</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>4</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2.5</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p>Đồ &aacute;n tốt nghiệp</p>\r\n			</td>\r\n			<td>\r\n			<p>8</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>8</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style="height:29px; width:331px">\r\n			<p><strong>Tổng cộng</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>158</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>14</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>172</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>4. Kế hoạch học trong 5 năm</strong></p>\r\n\r\n<p><em>Nội dung chi tiết của kế hoạch&nbsp;đề nghị xem tại tệp đ&iacute;nh k&egrave;m./.</em></p>\r\n\r\n<p><strong>5. C&aacute;c sinh hoạt kh&aacute;c</strong></p>\r\n\r\n<p><strong><em>Trường c&oacute; c&aacute;c c&acirc;u lạc bộ v&agrave; cơ sở vật chất phục vụ cho c&aacute;c hoạt động văn-thể-mỹ v&agrave; c&aacute;c hoạt động kh&aacute;c:</em></strong></p>\r\n\r\n<p>+ Thể thao: b&oacute;ng đ&aacute;, b&oacute;ng chuyền, b&oacute;ng b&agrave;n. . . tổ chức h&agrave;ng năm;<br />\r\n+ Văn nghệ: Ca, m&uacute;a nhạc;<br />\r\n+ C&acirc;u lạc bộ học thuật kh&aacute;c: Tiếng anh&hellip;</p>\r\n</div>', 1, 'vn', 1, 8, 'news', 57, 1441213054, NULL, NULL, 1),
(69, 'Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật xây dựng Cầu', 'f89e021f90ac4d9076770502c99ffd41_w250_h170_cp.jpg', '- Có kiến thức khoa học cơ bản và cơ sở cần thiết để tiếp thu các kiến thức chuyên ngành và khả năng học tập ở trình độ cao hơn (Thạc sĩ, Tiến sĩ); - Có kiến thức chuyên môn toàn diện để giải quyết các vấn đề: Lập và quản lý dự án đầu tư xây dựng công trình; Thiết kế, thiết kế thi công, chỉ đạo thi công, kiểm định chất lượng, khai thác và bảo trì công trình cầu;', '<p><strong>1. Mục ti&ecirc;u đ&agrave;o tạo chung của chuy&ecirc;n ng&agrave;nh</strong></p>\r\n\r\n<p><em><strong>1.1. Kiến thức</strong></em></p>\r\n\r\n<p>- C&oacute; kiến thức khoa học cơ bản v&agrave; cơ sở cần thiết để tiếp thu c&aacute;c kiến thức chuy&ecirc;n ng&agrave;nh v&agrave; khả năng học tập ở tr&igrave;nh độ cao hơn (Thạc sĩ, Tiến sĩ);<br />\r\n- C&oacute; kiến thức chuy&ecirc;n m&ocirc;n to&agrave;n diện để giải quyết c&aacute;c vấn đề: Lập v&agrave; quản l&yacute; dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh; Thiết kế, thiết kế thi c&ocirc;ng, chỉ đạo thi c&ocirc;ng, kiểm định chất lượng, khai th&aacute;c v&agrave; bảo tr&igrave; c&ocirc;ng tr&igrave;nh cầu;</p>\r\n\r\n<p><em><strong>1.2. Kỹ năng</strong></em></p>\r\n\r\n<p>- Tổ chức c&ocirc;ng t&aacute;c đo đạc khảo s&aacute;t thu thập số liệu cần thiết cho thiết kế c&ocirc;ng tr&igrave;nh cầu;<br />\r\n- Lập, tổ chức thực hiện, quản l&yacute; c&aacute;c dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh cầu;<br />\r\n- Thiết kế; thiết kế thi c&ocirc;ng; tổ chức thi c&ocirc;ng; kiểm tra v&agrave; nghiệm thu chất lượng c&ocirc;ng tr&igrave;nh cầu;<br />\r\n- Quản l&yacute;, khai th&aacute;c, kiểm định được chất lượng c&ocirc;ng tr&igrave;nh cầu;<br />\r\n- Sử dụng th&agrave;nh thạo c&aacute;c phần mềm chuy&ecirc;n ng&agrave;nh trong thiết kế, thi c&ocirc;ng, quản l&yacute; khai th&aacute;c c&ocirc;ng tr&igrave;nh cầu;<br />\r\n- Hội nhập được với m&ocirc;i trường quốc tế trong lĩnh vực c&ocirc;ng nghệ kỹ thuật x&acirc;y dựng cầu;<br />\r\n- Sử dụng ngoại ngữ, c&ocirc;ng nghệ th&ocirc;ng tin, để nghi&ecirc;n cứu, ứng dụng hiệu quả tiến bộ khoa học - c&ocirc;ng nghệ trong lĩnh vực c&ocirc;ng nghệ kỹ thuật x&acirc;y dựng cầu.</p>\r\n\r\n<p><strong>2. C&aacute;c c&ocirc;ng vi&ecirc;̣c sinh vi&ecirc;n phụ tr&aacute;ch sau t&ocirc;́t nghi&ecirc;̣p</strong></p>\r\n\r\n<p><em><strong>Sinh vi&ecirc;n sau khi tốt nghiệp c&oacute; thể l&agrave;m việc ở c&aacute;c vị tr&iacute;:</strong></em></p>\r\n\r\n<p><em><strong><img alt="Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật xây dựng Cầu" src="http://utt.edu.vn/home/images/stories/chuong-trinh-dao-tao-chuyen-nganh-cong-nghe-ky-thuat-xay-dung-cau-01.jpg" style="height:732px; width:750px" /></strong></em></p>\r\n\r\n<p><strong>3. Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n v&agrave; nội dung chương tr&igrave;nh học:&nbsp;</strong></p>\r\n\r\n<p>- Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n: Chương tr&igrave;nh học c&oacute; t&iacute;nh hiện đại, t&iacute;nh mở, tiếp cận s&aacute;t với y&ecirc;u cầu thực tế đặt ra của ng&agrave;nh GTVT;<br />\r\n- Tổng số t&iacute;n chỉ v&agrave; khối kiến thức ph&acirc;n bổ: 167 T&iacute;n chỉ. Trong đ&oacute; Kiến thức đại cương 47 T&iacute;n chỉ, Kiến thức gi&aacute;o dục chuy&ecirc;n nghiệp (Cơ sở ng&agrave;nh, ng&agrave;nh, thực h&agrave;nh, thực tập, đồ &aacute;n) 120 T&iacute;n chỉ.</p>\r\n\r\n<p><strong>4. Kế hoạch học trong 5 năm</strong></p>\r\n\r\n<div>\r\n<p><em>Nội dung chi tiết của kế hoạch&nbsp;đề nghị xem tại tệp đ&iacute;nh k&egrave;m./.</em></p>\r\n\r\n<p><strong>5. C&aacute;c sinh hoạt kh&aacute;c</strong></p>\r\n\r\n<p><strong><em>Trường c&oacute; c&aacute;c c&acirc;u lạc bộ v&agrave; cơ sở vật chất phục vụ cho c&aacute;c hoạt động văn-thể-mỹ v&agrave; c&aacute;c hoạt động kh&aacute;c:</em></strong></p>\r\n\r\n<p>+ Thể thao: b&oacute;ng đ&aacute;, b&oacute;ng chuyền, b&oacute;ng b&agrave;n. . . tổ chức h&agrave;ng năm;<br />\r\n+ Văn nghệ: Ca, m&uacute;a nhạc;<br />\r\n+ C&acirc;u lạc bộ học thuật kh&aacute;c: Tiếng anh&hellip;</p>\r\n</div>', 1, 'vn', 1, 8, 'news', 57, 1441213071, NULL, NULL, 1),
(70, 'Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật công trình xây dựng dân dụng và công nghiệp', '5b2154834ba95ec49ab42973f3f2fecb_w250_h170_cp.jpg', 'Chương trình đào tạo đại học Công nghệ kỹ thuật Công trình xây dựng Dân dụng và Công nghiệp nhằm trang bị cho người học sau khi tốt nghiệp có kiến thức khoa học cơ bản, cơ sở, kỹ thuật chuyên môn toàn diện, năng lực thực hành nghề nghiệp cơ bản, năng lực tư duy sáng tạo, khả năng thích ứng nhanh với những biến đổi trong lĩnh vực công nghệ xây dựng dân dụng và công nghiệp; có đạo đức nghề nghiệp, có sức khỏe đáp ứng yêu cầu xây dựng và bảo vệ Tổ quốc.', '<p>Chương tr&igrave;nh đ&agrave;o tạo đại học C&ocirc;ng nghệ kỹ thuật C&ocirc;ng tr&igrave;nh x&acirc;y dựng D&acirc;n dụng v&agrave; C&ocirc;ng nghiệp nhằm trang bị cho người học sau khi tốt nghiệp c&oacute; kiến thức khoa học cơ bản, cơ sở, kỹ thuật chuy&ecirc;n m&ocirc;n to&agrave;n diện, năng lực thực h&agrave;nh nghề nghiệp cơ bản, năng lực tư duy s&aacute;ng tạo, khả năng th&iacute;ch ứng nhanh với những biến đổi trong lĩnh vực c&ocirc;ng nghệ x&acirc;y dựng d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp; c&oacute; đạo đức nghề nghiệp, c&oacute; sức khỏe đ&aacute;p ứng y&ecirc;u cầu x&acirc;y dựng v&agrave; bảo vệ Tổ quốc.</p>\r\n\r\n<p><strong>1. Mục ti&ecirc;u đ&agrave;o tạo chung của chuy&ecirc;n ng&agrave;nh</strong></p>\r\n\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo đại học C&ocirc;ng nghệ kỹ thuật C&ocirc;ng tr&igrave;nh x&acirc;y dựng D&acirc;n dụng v&agrave; C&ocirc;ng nghiệp nhằm trang bị cho người học sau khi tốt nghiệp c&oacute; kiến thức khoa học cơ bản, cơ sở, kỹ thuật chuy&ecirc;n m&ocirc;n to&agrave;n diện, năng lực thực h&agrave;nh nghề nghiệp cơ bản, năng lực tư duy s&aacute;ng tạo, khả năng th&iacute;ch ứng nhanh với những biến đổi trong lĩnh vực c&ocirc;ng nghệ x&acirc;y dựng d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp; c&oacute; đạo đức nghề nghiệp, c&oacute; sức khỏe đ&aacute;p ứng y&ecirc;u cầu x&acirc;y dựng v&agrave; bảo vệ &nbsp;Tổ quốc.</p>\r\n\r\n<p><strong>2. C&aacute;c c&ocirc;ng vi&ecirc;̣c sinh vi&ecirc;n phụ tr&aacute;ch sau t&ocirc;́t nghi&ecirc;̣p</strong></p>\r\n\r\n<p>+ Thi c&ocirc;ng v&agrave; gi&aacute;m s&aacute;t thi c&ocirc;ng c&ocirc;ng tr&igrave;nh;<br />\r\n+ Tư vấn thiết kế x&acirc;y dựng c&ocirc;ng tr&igrave;nh;<br />\r\n+ Tư vấn khảo s&aacute;t c&ocirc;ng tr&igrave;nh;<br />\r\n+ Kiểm định chất lượng c&ocirc;ng tr&igrave;nh.</p>\r\n\r\n<p><img alt="Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật công trình xây dựng dân dụng và công nghiệp" src="http://utt.edu.vn/home/images/stories/chuong-trinh-dao-tao-chuyen-nganh-cong-nghe-ky-thuat-cong-trinh-xay-dung-dan-dung-va-cong-nghiep-01.jpg" style="height:502px; width:752px" /></p>\r\n\r\n<p><strong>3. Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n v&agrave; nội dung chương tr&igrave;nh học</strong></p>\r\n\r\n<p>- Quan điểm x&acirc;y dựng chương tr&igrave;nh học cho sinh vi&ecirc;n l&agrave; chương tr&igrave;nh đ&agrave;o tạo b&aacute;m s&aacute;t mục ti&ecirc;u đ&agrave;o tạo &quot;Kỹ sư thực h&agrave;nh&quot;, gi&uacute;p sinh vi&ecirc;n nắm bắt v&agrave; l&agrave;m chủ c&ocirc;ng nghệ. C&oacute; khả năng b&aacute;m s&aacute;t thực tế ngay sau khi ra trường.<br />\r\n- Tổng số t&iacute;n chỉ phải t&iacute;ch lũy: 163 t&iacute;n chỉ (TC), hai m&ocirc;n học bắt buộc l&agrave; Gi&aacute;o dục thể chất (5 đơn vị học tr&igrave;nh) v&agrave; Gi&aacute;o dục Quốc ph&ograve;ng &ndash; An ninh (8 t&iacute;n chỉ) sẽ được cấp chứng chỉ ri&ecirc;ng.<br />\r\n- C&aacute;c học phần thực h&agrave;nh thực tập:</p>\r\n\r\n<div>\r\n<table border="1">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>STT</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>T&Ecirc;N HỌC PHẦN</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>SỐ TC</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>I</strong></p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p><strong>Thực h&agrave;nh, thực tập nghề nghiệp</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>14 TC</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>1</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực h&agrave;nh trắc địa</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>2</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>2</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực h&agrave;nh th&iacute; nghiệm VLXD, địa chất v&agrave; cơ học đất</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>3</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>3</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực tập kỹ thuật thi c&ocirc;ng cơ bản</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>3</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>4</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực tập nghề nghiệp x&acirc;y dựng c&ocirc;ng tr&igrave;nh</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>4</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>5</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực tập th&iacute; nghiệm kiểm định c&ocirc;ng tr&igrave;nh</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>2</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>II</strong></p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p><strong>Thực tập tốt nghiệp</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>04 TC</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>6</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Thực tập tốt nghiệp</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>4</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>III</strong></p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p><strong>Đồ &aacute;n tốt nghiệp</strong></p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p><strong>08 TC</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>7</p>\r\n			</td>\r\n			<td style="vertical-align:middle">\r\n			<p>Đồ &aacute;n tốt nghiệp</p>\r\n			</td>\r\n			<td style="text-align:center; vertical-align:middle">\r\n			<p>8</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>+ C&aacute;c học phần h&agrave;nh tập trắc địa v&agrave; &nbsp;th&iacute; nghiệm VLXD, địa chất, cơ đất, sinh vi&ecirc;n được sử dụng c&aacute;c loại m&aacute;y m&oacute;c thiết bị hiện đại do dự &aacute;n Jica của ch&iacute;nh phủ Nhật cung cấp.<br />\r\n+ Đặc biệt với học phần thực tập nghề nghiệp, sinh vi&ecirc;n sẽ được trực tiếp tham gia thực tập tại xưởng C&ocirc;ng tr&igrave;nh của Trường để n&acirc;ng cao kỹ năng v&agrave; tay nghề.<br />\r\n+ Với học phần Tin học ứng dụng, sinh vi&ecirc;n được trực tiếp thực h&agrave;nh ở ph&ograve;ng m&aacute;y t&iacute;nh, học phần sẽ cung cấp kỹ năng sử dụng c&aacute;c phần mềm cần thiết, đảm bảo cho sinh vi&ecirc;n c&oacute; khả năng bắt nhịp được với nhu cầu thực tế sản xuất.<br />\r\n+ Ngoại học phần Đồ &aacute;n tốt nghiệp với 8 TC v&agrave; thời gian l&agrave;m 12 tuần, sinh vi&ecirc;n c&ograve;n được thực hiện 5 đồ &aacute;n m&ocirc;n học gồm: Đồ &aacute;n BTCT, Đồ &aacute;n Nền v&agrave; m&oacute;ng, Đồ &aacute;n Kiến tr&uacute;c DD &amp; CN, Đồ &aacute;n Kết cấu nh&agrave;, Đồ &Aacute;n Thi c&ocirc;ng v&agrave; TCTC.</p>\r\n\r\n<p><strong>4. Kế hoạch học trong 5 năm</strong></p>\r\n\r\n<p>Trung b&igrave;nh mỗi học kỳ, sinh vi&ecirc;n sẽ được học 12-14 t&iacute;n chỉ, c&aacute;c sinh vi&ecirc;n c&oacute; th&agrave;nh t&iacute;ch tốt c&oacute; thể đăng k&yacute; để học vượt, r&uacute;t ngắn thời gian học tập.</p>\r\n\r\n<p><img alt="Chương trình đào tạo Chuyên ngành Công nghệ kỹ thuật công trình xây dựng dân dụng và công nghiệp" src="http://utt.edu.vn/home/images/stories/chuong-trinh-dao-tao-chuyen-nganh-cong-nghe-ky-thuat-cong-trinh-xay-dung-dan-dung-va-cong-nghiep-02.jpg" style="display:block; height:559px; margin-left:auto; margin-right:auto; width:750px" /></p>\r\n\r\n<p><strong>5. C&aacute;c sinh hoạt kh&aacute;c</strong></p>\r\n\r\n<p><em><strong>Trường c&oacute; c&aacute;c c&acirc;u lạc bộ v&agrave; cơ sở vật chất phục vụ cho c&aacute;c hoạt động văn-thể-mỹ v&agrave; c&aacute;c hoạt động kh&aacute;c:</strong></em></p>\r\n\r\n<p>+ Thể thao: b&oacute;ng đ&aacute;, b&oacute;ng chuyền, b&oacute;ng b&agrave;n. . . tổ chức h&agrave;ng năm;<br />\r\n+ Văn nghệ: Ca, m&uacute;a nhạc;<br />\r\n+ C&acirc;u lạc bộ học thuật kh&aacute;c: Tiếng anh&hellip;</p>\r\n</div>', 1, 'vn', 1, 8, 'news', 57, 1441213145, NULL, NULL, 1),
(71, 'Mô hình đào tạo', '010_-_So_do_to_chuc_utt1.jpg', 'Mô hình đào tạo', '<div>&nbsp;</div>\\r\\n\\r\\n<p><img alt=\\"\\" src=\\"http://utt.edu.vn/home/images/stories/mo-hinh-dao-tao-utt-2015.jpg\\" style=\\"display:block; height:578px; margin-left:auto; margin-right:auto; width:801px\\" /></p>', 1, 'vn', 1, 8, 'news', 5, 1441213206, 1441213281, NULL, 1),
(72, 'Báo cáo thực hiện quy chế 3 công khai năm học 2011 - 2012', 'defaut_thumbnail_utt.jpg', 'Thực hiện ý kiến chỉ đạo tại công văn số 7422/BGDĐT-KHTC ngày 04/11/2011 của Bộ Giáo dục & Đào tạo về việc báo cáo các nội dung thực hiện quy chế công khai năm học 2011-2012, Trường Đại học Công nghệ Giao thông vận tải công bố các nội dung chính công khai như sau:', '<p>Thực hiện &yacute; kiến chỉ đạo tại c&ocirc;ng văn số 7422/BGDĐT-KHTC ng&agrave;y 04/11/2011 của Bộ Gi&aacute;o dục &amp; Đ&agrave;o tạo về việc b&aacute;o c&aacute;o c&aacute;c nội dung thực hiện quy chế c&ocirc;ng khai năm học 2011-2012, Trường Đại học C&ocirc;ng nghệ Giao th&ocirc;ng vận tải c&ocirc;ng bố c&aacute;c nội dung c&ocirc;ng khai ch&iacute;nh như sau:<br />\r\n1.&nbsp;Biểu tổng hợp một số th&ocirc;ng tin c&ocirc;ng khai của cơ sở gi&aacute;o dục đại học, cao đẳng năm học 2011-2012 <em>(Biểu tổng hợp đ&iacute;nh k&egrave;m).</em><br />\r\n2.&nbsp;C&ocirc;ng khai cam kết chất lượng đ&agrave;o tạo của cơ sở gi&aacute;o dục đại học, cao đẳng năm học 2011-2012 (<em>Biểu mẫu 20 đ&iacute;nh k&egrave;m</em>).<br />\r\n3. C&ocirc;ng khai chất lượng đ&agrave;o tạo thực tế của cơ sở gi&aacute;o dục đại học, cao đẳng (<em>Biểu mẫu 21 đ&iacute;nh k&egrave;m</em>).<br />\r\n4.&nbsp;C&ocirc;ng khai cơ sở vật chất của cơ sở gi&aacute;o dục đại học, cao đẳng (<em>Biểu mẫu 22 đ&iacute;nh k&egrave;m).</em><br />\r\n5. C&ocirc;ng khai th&ocirc;ng tin về đội ngũ giảng vi&ecirc;n cơ hữu của cơ sở gi&aacute;o dục đại học, cao đẳng <em>(Biểu mẫu 23 đ&iacute;nh k&egrave;m).</em><br />\r\n6. C&ocirc;ng khai thu chi t&agrave;i ch&iacute;nh&nbsp;của cơ sở gi&aacute;o dục đại học, cao đẳng <em>(Biểu mẫu 24 đ&iacute;nh k&egrave;m).</em></p>', 1, 'vn', 1, 8, 'news', 59, 1441213328, NULL, NULL, 1),
(73, 'Nhu cầu nguồn nhân lực ngành GTVT', '8ffacbe7007af74bf22da65d812ab008_w250_h170_cp1.jpg', 'Theo thống kê và những số liệu thực tế do Viện chiến lược và Phát triển GTVT, Bộ Giao thông vận tải công bố thì dự báo nhu cầu nguồn nhân lực cần cho sự phát triển của Ngành GTVT trong những năm tới là rất lớn.', '<p>Theo thống k&ecirc; v&agrave; những số liệu thực tế do Viện chiến lược v&agrave; Ph&aacute;t triển GTVT, Bộ Giao th&ocirc;ng vận tải c&ocirc;ng bố th&igrave; dự b&aacute;o nhu cầu nguồn nh&acirc;n lực cần cho sự ph&aacute;t triển của Ng&agrave;nh GTVT trong những năm tới l&agrave; rất lớn.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng t&igrave;m hiểu cụ thể theo từng khối ng&agrave;nh dưới đ&acirc;y:</p>\r\n\r\n<div>\r\n<h1>1. Khối ng&agrave;nh C&ocirc;ng tr&igrave;nh</h1>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<h3>X&acirc;y dựng DD&amp;CN</h3>\r\n	</li>\r\n	<li>\r\n	<h3>XD Cảng &ndash; Đường thủy</h3>\r\n	</li>\r\n	<li>\r\n	<h3>XD Cầu đường sắt</h3>\r\n	</li>\r\n	<li>\r\n	<h3>XD Cầu đường bộ</h3>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<h2>Ng&agrave;nh: C&ocirc;ng nghệ kỹ thuật (CNKT) C&ocirc;ng tr&igrave;nh x&acirc;y dựng | M&atilde; ng&agrave;nh: 52510102</h2>\r\n</div>\r\n\r\n<p>C&aacute;c chuy&ecirc;n ng&agrave;nh:</p>\r\n\r\n<p>✔ CNKT C&ocirc;ng tr&igrave;nh x&acirc;y dựng d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp</p>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl07.jpg" style="height:300px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl08.jpg" style="height:313px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<blockquote>\r\n<p>Hiện tại, nhu cầu x&acirc;y dựng nh&agrave; ở, đặc biệt l&agrave; nh&agrave; ở x&atilde; hội đang rất cao.</p>\r\n<small>Theo Bộ X&acirc;y dựng <a href="http://www.xaydung.gov.vn/en/trang-chi-tiet/-/tin-chi-tiet/Z2jG/1/260540/nhu-cau-nha-o-xa-hoi-la-rat-cao.html" target="_blank"><cite>Chi tiết tại đ&acirc;y</cite></a></small></blockquote>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<p>Cơ hội việc l&agrave;m:</p>\r\n\r\n<div>\r\n<div>\r\n<h3>CNKT C&ocirc;ng tr&igrave;nh x&acirc;y dựng d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp</h3>\r\n</div>\r\n\r\n<div>\r\n<p>Người học sau khi tốt nghiệp c&oacute; thể l&agrave;m việc ở vị tr&iacute; kỹ sư tại c&aacute;c doanh nghiệp x&acirc;y dựng, c&ocirc;ng ty tư vấn, ban quản l&yacute; dự &aacute;n, viện nghi&ecirc;n cứu, trung t&acirc;m kiểm định đ&aacute;nh gi&aacute; chất lượng c&ocirc;ng tr&igrave;nh x&acirc;y dựng d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<h1>2. Khối ng&agrave;nh Cơ kh&iacute;</h1>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<h3>Cơ điện tử</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Cơ kh&iacute; Đầu m&aacute;y &ndash; Toa xe</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Cơ kh&iacute; M&aacute;y t&agrave;u thủy</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Cơ kh&iacute; M&aacute;y x&acirc;y dựng</h3>\r\n	</li>\r\n	<li>\r\n	<h3>CNKT &Ocirc; t&ocirc;</h3>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<h2>Ng&agrave;nh: C&ocirc;ng nghệ kỹ thuật Cơ điện tử | M&atilde; ng&agrave;nh: 52510203</h2>\r\n</div>\r\n\r\n<p>C&aacute;c chuy&ecirc;n ng&agrave;nh:</p>\r\n\r\n<p>✔ C&ocirc;ng nghệ kỹ thuật Cơ điện tử</p>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl17.jpg" style="height:300px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl18.jpg" style="height:313px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<blockquote>\r\n<p>Ước t&iacute;nh đến năm 2020, cả nước c&oacute; khoảng 2,8 &divide; 3 triệu xe &ocirc; t&ocirc; c&aacute;c loại, trong đ&oacute; xe &ocirc; t&ocirc; con 1,5 triệu chiếc, xe &ocirc; t&ocirc; kh&aacute;ch 0,5 triệu chiếc, xe &ocirc; t&ocirc; tải 0,8 triệu chiếc.</p>\r\n<small>Theo Viện chiến lược &amp; Ph&aacute;t triển GTVT <a href="http://tdsi.gov.vn/Default.aspx?tabid=0&amp;catid=341.354&amp;articleid=5185" target="_blank"><cite>Chi tiết tại đ&acirc;y</cite></a></small></blockquote>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<p>Cơ hội việc l&agrave;m:</p>\r\n\r\n<div>\r\n<div>\r\n<h3>C&ocirc;ng nghệ kỹ thuật Cơ điện tử</h3>\r\n</div>\r\n\r\n<div>\r\n<p>Người học sau khi tốt nghiệp c&oacute; thể l&agrave;m việc ở c&aacute;c viện thiết kế, c&aacute;c cơ sở chế tạo, bảo dưỡng, sửa chữa &ocirc; t&ocirc;, c&aacute;c ban quản l&yacute; dự &aacute;n, khu c&ocirc;ng nghiệp, cơ quan quản l&yacute; nh&agrave; nước về c&ocirc;ng nghiệp v&agrave; GTVT.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<h1>3. Khối ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, Điện tử viễn th&ocirc;ng</h1>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<h3>Truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Điện tử, truyền th&ocirc;ng</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Hệ thống th&ocirc;ng tin</h3>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<h2>Ng&agrave;nh: Truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh | M&atilde; ng&agrave;nh: 52480102</h2>\r\n</div>\r\n\r\n<p>C&aacute;c chuy&ecirc;n ng&agrave;nh:</p>\r\n\r\n<p>✔ Truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh</p>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl23.jpg" style="height:300px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl24.jpg" style="height:313px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<blockquote>\r\n<p>Hiện tại, c&aacute;c tập đo&agrave;n quốc tế (Intel, Renesas, LSI, SDS, Shinko&hellip;) hoặc c&aacute;c c&ocirc;ng ty lớn ở Việt Nam (VNPT, Viettel, MobiFone, Vinaphone, FPT&hellip;) đều c&oacute; nhu cầu rất cao.</p>\r\n</blockquote>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<p>Cơ hội việc l&agrave;m:</p>\r\n\r\n<div>\r\n<div>\r\n<h3>Truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh</h3>\r\n</div>\r\n\r\n<div>\r\n<p>Người học sau khi tốt nghiệp c&oacute; thể l&agrave;m việc tại c&aacute;c đơn vị quản trị hệ thống truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh trong c&aacute;c viện, trung t&acirc;m nghi&ecirc;n cứu; thiết kế, c&agrave;i đặt, bảo tr&igrave; v&agrave; đảm bảo an ninh cho c&aacute;c hệ thống truyền th&ocirc;ng v&agrave; mạng m&aacute;y t&iacute;nh, hệ thống ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin kh&aacute;c trong c&aacute;c cơ quan, doanh nghiệp.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<h1>4. Khối ng&agrave;nh Kinh tế vận tải</h1>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<ul>\r\n	<li>\r\n	<h3>Quản trị doanh nghiệp</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Kế to&aacute;n doanh nghiệp</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Kinh tế x&acirc;y dựng</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Khai th&aacute;c vận tải</h3>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<div>\r\n<h2>Ng&agrave;nh: Quản trị kinh doanh | M&atilde; ng&agrave;nh: 52340101</h2>\r\n</div>\r\n\r\n<p>C&aacute;c chuy&ecirc;n ng&agrave;nh:</p>\r\n\r\n<p>✔ Quản trị doanh nghiệp</p>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl27.jpg" style="height:300px; width:456px" /></div>\r\n</div>\r\n\r\n<div>\r\n<div><img alt="" src="http://utt.edu.vn/home/images/stories/ncnnl28.jpg" style="height:313px; width:456px" /></div>\r\n</div>', 1, 'vn', 1, 8, 'news', 60, 1441213381, NULL, NULL, 1),
(75, 'Công ty Công nghệ TDT', '1045018_353547914772367_1394040617_n.jpg', NULL, NULL, 1, NULL, 2, 8, 'partner', 0, 1441297397, NULL, NULL, NULL);
INSERT INTO `utt_post` (`id`, `title`, `image`, `description`, `detail`, `site_id`, `lang`, `status`, `user_id`, `post_type`, `cate_id`, `time_create`, `time_update`, `sort`, `is_top`) VALUES
