INSERT INTO `utt_slide` (`id`, `img`, `url`, `site_id`, `status`, `location`, `description`, `lang`, `title`, `post_id`) VALUES
(1, '1441296122doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-03.jpg', 'http://localhost/utt/utt/tin-tuc-su-kien/doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-a20.html', 1, 1, 3, 'Kỳ thi tay nghề thế giới lần thứ 43 sẽ được tổ chức tại Sao Paulo, Brazil từ ngày 5 đến ngày 17/8/2015 ở 50 nghề, trong đó có nghề Thiết kế kỹ thuật cơ khí - CAD. Hiện đã có 1.273 thí sinh của 72 quốc gia, vùng lãnh thổ đăng ký tham dự. Đoàn Việt Nam đã có sự chuẩn bị khá sớm và đang bước vào giai đoạn "nước rút" với quyết tâm đạt thành tích cao nhất tại kỳ thi.', 'vn', '', 0),
(2, '1441296746doan-cong-tac-nghe-thiet-ke-ky-thuat-co-khi-cad-truong-dai-hoc-cong-nghe-gtvt-lam-viec-tai-han-quoc-03.jpg', 'http://localhost/utt/utt/news-and-events/leaders-welcomed-and-congratulated-school-students-returned-from-america-world-skills-competition-2015-a14.html', 1, 1, 1, 'This afternoon (19/8), immediately after the welcome at the airport, the University of Technology of Transport has organized a meeting to congratulate and reward student Nguyen Duc Thanh at world skills competitions th 43 held in Sao Paulo, Brazil. On behalf of the University, Prof. Dao Van East - Rector congratulated and rewarded for student Nguyen Duc Thanh (Class 62CCOT04, industry automotive engineering) achieved Skills Certificate world excellent technical disciplines mechanical design - CAD and lecturers Bui Xuan Tung (Center for Engineering Technology) specializes in the contest.', 'en', '', 0),
(3, '1441296602bo-gtvt-long-trong-to-chuc-le-ky-niem-70-nam-031.jpg', 'http://localhost/utt/utt/news-and-events/700-student-utt-be-performing-at-celebration-of-70-years-of-tradition-and-patriotic-emulation-congress-transportation-sector-a12.html', 1, 1, 2, 'Morning date 08/22/2015, at the National Conference Center in Hanoi My Dinh-TP, the Ministry of Transport (MOT) organized a solemn celebration of 70 years of tradition (28/8 / 1945-28 / 8/2015) and the Congress of patriotic emulation transport sector. 700 students of Technology University of Transport has participated in performances, serve and organize the program.', 'en', '', 0),
(4, '1441296963truong-dai-hoc-cong-nghe-gtvt-to-chuc-hoi-thao-tu-van-gioi-thieu-viec-lam-va-phong-van-tuyen-dung-cua-doanh-nghiep-nhat-ban-031.jpg', 'http://localhost/utt/utt/tin-tuc-su-kien/to-chuc-lop-boi-duong-ky-nang-soan-thao-van-ban-cho-can-bo-vien-chuc-nha-truong-a19.html', 1, 1, 4, 'Thực hiện kế hoạch đào tạo, bồi dưỡng công chức, viên chức năm 2015, chiều ngày 24/7/2015, Trường Đại học Công nghệ GTVT tổ chức lớp bồi dưỡng kỹ năng soạn thảo văn bản cho cán bộ làm hành chính và giảng viên kiêm nhiệm thuộc các khoa, trung tâm và phòng chức năng tại hội trường tầng 4 Thư viện của Trường.', 'vn', '', 0),
(5, '1441332781banner-xet-tuyen-bo-sung-2015.png', 'http://localhost/utt/utt/tuyen-sinh/tuyen-sinh/thong-bao-xet-tuyen-nguyen-vong-bo-sung-he-dai-hoc-2015-a76.html', 1, 1, 1, 'Thông  báo tuyển sinh', 'vn', '', 0),
(6, '1441333154banner_mung_quoc_khanh.png', 'http://localhost/utt/', 1, 1, 2, 'Chào mừng 70 năm Cách mạng tháng 8 và Quốc khánh 2-9', 'vn', '', 0);
